import React, { useState, useEffect } from "react";
import styled from "styled-components";
import KakaoMap from "./KaKaoMap";
import Marker from "./Marker";
import { useQuery } from "@tanstack/react-query";

const MapBox = styled.div`
  width: 100%;
  align-items: center;
  margin: 0;

  @media (max-width: 560px) {
    height: calc(110vw + 50px);
  }

  @media (min-width: 561px) {
    height: 666px;
  }
`;

const fetchUserLocation = () => {
  return new Promise((resolve, reject) => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          resolve({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          });
        },
        (err) => {
          reject(new Error("geolocation을 사용할 수 없어요."));
        }
      );
    } else {
      reject(new Error("geolocation을 지원하지 않습니다."));
    }
  });
};

const MapTemplate = () => {
  const [flag, setFlag] = useState(false); // marker 활성화를 위한 flag 유지

  const {
    data: userLocation,
    isError: locationError,
    isLoading: locationLoading,
  } = useQuery({
    queryKey: ["userLocation"],
    queryFn: fetchUserLocation,
  });

  const { positions, isLoading: markersLoading } = Marker(); // Marker에서 positions과 로딩 상태 받아옴

  useEffect(() => {
    if (!locationLoading && !markersLoading && userLocation) {
      setFlag(true); // 모든 로딩이 완료되었을 때 flag를 true로 설정
    }
  }, [locationLoading, markersLoading, userLocation]);

  if (locationError) {
    return <div>{locationError.message}</div>;
  }

  return (
    <MapBox>
      {flag && (
        <KakaoMap
          lat={userLocation.lat}
          lng={userLocation.lng}
          flag={flag} // showMarker에 flag 값 전달
          galleriesMarker={positions} // 갤러리 마커 데이터 전달
        />
      )}
    </MapBox>
  );
};

export default MapTemplate;
