import React, { useState, useEffect, useCallback, useRef } from "react";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import PostTemplate from "../post/PostTemplate";
import SearchBar from "../button/SearchBar";
import styled from "styled-components";
import HorizontalLine from "../layout/HorizontalLine";
import { fetchShowsbySearchQuery } from "../../lib/api/Api";

const SearchContainer = styled.div`
  display: flex;
  align-items: center;
`;

const ShowContent = styled.div`
  text-align: left;
  font-size: 40px;
  font-weight: bold;
  // line-height: 1.2px;
  padding: 6px 0 0 18px;
  color: white;
  font-family: "Happiness-Sans-Title", sans-serif;
`;

const DefaultShow = styled.div`
  color: white;
  padding-left: 12px;
  height: 400px;
  p {
    margin 0;
    margin-bottom: 8px;
    font-size: 22px;
  }
`;

const NoneShow = styled.div`
  color: white;
  padding-left: 0.8rem;
  height: 400px;
  p {
    margin 0;
    margin-bottom: 0.4rem;
    font-size: 1.4rem;
  }
`;

const SelectArea = styled.div`
  margin-left: auto;
  margin-right: 15px;
  margin-top: 10px;
`;

const darkTheme = createTheme({
  palette: {
    mode: "dark", // Set the theme mode to dark
  },
});

function Search() {
  const [shows, setShows] = useState(null);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  const [selectedValue, setSelectedValue] = useState("1");
  const prevSelectedValueRef = useRef(selectedValue);

  const onSearch = useCallback(
    (searchString) => setSearchQuery(searchString),
    []
  );
  useEffect(() => {
    const storedSelectedValue = localStorage.getItem("selectedValue");
    if (storedSelectedValue) {
      setSelectedValue(storedSelectedValue);
    }
  }, []);

  useEffect(() => {
    const fetchShows = async () => {
      try {
        setError(null);
        setShows(null);
        const showsData = await fetchShowsbySearchQuery(
          searchQuery,
          selectedValue
        );
        setShows(showsData);
      } catch (e) {
        setError(e);
      }
    };

    fetchShows();
  }, [searchQuery]);

  useEffect(() => {
    const fetchShowsByValue = async () => {
      try {
        setError(null);
        setShows(null);
        const showsData = await fetchShowsbySearchQuery(
          searchQuery,
          selectedValue
        );
        setShows(showsData);
      } catch (e) {
        setError(e);
      }
    };

    if (prevSelectedValueRef.current !== selectedValue) {
      fetchShowsByValue();
      prevSelectedValueRef.current = selectedValue; // Update the stored previous value
    }
  }, [selectedValue]);

    useEffect(() => {
      localStorage.setItem("selectedValue", selectedValue);
    }, [selectedValue]);

  if (error)
    return (
      <>
        <SearchBar onSearch={onSearch} />
        <HorizontalLine key="horizontal-line" />
        <NoneShow>
          <p>ERROR!</p>
        </NoneShow>
      </>
    );
  if (!shows)
    return (
      <>
        <SearchBar onSearch={onSearch} />
        <HorizontalLine key="horizontal-line" />
        <DefaultShow>
          <p>NO SHOWS!</p>
          <p></p>
        </DefaultShow>
      </>
    );
  if (shows.length === 0)
    return (
      <>
        <SearchBar onSearch={onSearch} />
        <HorizontalLine key="horizontal-line" />
        <NoneShow>
          <p>검색 결과에 맞는 전시가 없습니다.</p>
          <p>다른 검색어를 입력해주세요.</p>
        </NoneShow>
      </>
    );
  if (shows.length !== 0) {
    return (
      <>
        <SearchBar onSearch={onSearch} />
        <HorizontalLine key="horizontal-line" />
        <ThemeProvider theme={darkTheme}>
          <SearchContainer>
            <ShowContent>Exhibitions</ShowContent>

            <SelectArea>
              <FormControl>
                <Select
                  value={selectedValue} // Use the selectedValue state here
                  onChange={(event) => setSelectedValue(event.target.value)} // Update the selectedValue state
                  size="small"
                  variant="outlined"
                  color="primary"
                >
                  <MenuItem value="1">최신순</MenuItem>
                  <MenuItem value="2">마감순</MenuItem>
                </Select>
              </FormControl>
            </SelectArea>
          </SearchContainer>
        </ThemeProvider>
        {shows && <PostTemplate shows={shows} />}
      </>
    );
  }
  return (
    <>
      <SearchBar onSearch={onSearch} />
      <HorizontalLine key="horizontal-line" />
      <NoneShow>
        <p>검색 결과에 맞는 전시가 없습니다.</p>
        <p>다른 검색어를 입력해주세요.</p>
      </NoneShow>
    </>
  );
}

export default React.memo(Search);
