import styled from "styled-components";
import Skeleton from "@mui/material/Skeleton";
import Stack from "@mui/material/Stack";
import colorSet from "../../lib/styles/colorSet";

const TemplateBlock = styled.div`
  padding-top: 2px;
  box-sizing: border-box;
  align-items: center;
  flex-flow: row wrap;
`;
const PostBlock = styled.div`
  padding: 5px 14px 19px 14px;
  width: 100%;
  margin: 0 auto;
`;

const ContextBoxes = styled.div`
  margin: 0;
  width: 100%;
  max-width: 720px;
`;

const HoverEffect = styled.div`
//   background: #191919;
  background: #191919;
  // background: #000000;
  // border: 1px solid #6a6a6a;
  &:hover {
  background: ${(props) => props.color || "#ec6c03"};
  h1,
  h2,
  h3 {
    color: black;
    text-decoration: underline;
  }
  h4,
  h5 {
    color: black;
  }
  strong {
    color: black;
  }

  img {
    transform: scale(1.1);
  }
`;

const ContextBox = styled.div`
  padding: 1px 0 8px 15px;
  flex: 1 0 auto;
`;

const PostTitle = styled.div`
  text-align: left;
  color: white;
  strong {
    color: ${(props) => props.color || "#ec6c03"};
  }
  font-family: "Happiness-Sans-Title", sans-serif;
`;

const PostContent = styled.div`
  text-align: left;
  font-family: "Happiness-Sans-Bold", sans-serif;
  padding-bottom: 0.5rem;
  h1,
  h2,
  h3,
  h4 {
    padding-top: 1.24rem;
    color: white;
    margin: 0 0 1rem 0;
  }
  h5 {
    color: lightgray;
    margin: 0 0.5rem 0rem 0;
    display: inline-block;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }
`;

const ImageBlock = styled.div`
  width: 100%;
  // height: 0;
  // padding-bottom: 56.25%; /* maintain 16:9 aspect ratio */
  aspect-ratio: 16/9;
  position: relative;
  bottom: 0px;
  overflow: hidden;
  img {
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.6s ease;
  }
`;
const PostSkeleton = () => {
  return (
    <TemplateBlock>
      {colorSet.map((color) => {
        return (
          <PostBlock>
            <ContextBoxes>
              <HoverEffect color={color}>
                <ContextBox>
                  <PostTitle color={color}>
                    <h2>
                      <Skeleton variant="text" sx={{ fontSize: "1rem" }} />
                      <br />
                      <strong>show_name</strong>
                    </h2>
                  </PostTitle>
                  <PostContent>
                    <h4>show_term_start~ show_term_end</h4>
                    tags
                  </PostContent>
                </ContextBox>

                <ImageBlock>img</ImageBlock>
              </HoverEffect>
            </ContextBoxes>
          </PostBlock>
        );
      })}
    </TemplateBlock>
  );
};

export default PostSkeleton;
