import Calendar from "react-calendar";
import "./Calendar.css"; // css import
import styled from "styled-components";
import React, { Component } from "react";
import { useState } from "react";

const ColoredCalendar = styled(Calendar)``;

function MyCalendar({ date, color }) {
  const [value, onChange] = useState(new Date());
  const dateArray = date.split(" ~ ");

  const parsedDates = dateArray.map((dateString) => {
    const timestamp = Date.parse(dateString);
    return new Date(timestamp);
  });
  const [duringDate, setDuringDate] = useState(parsedDates);
  const currentDate = new Date();
  const oneYearLaterDate = new Date(currentDate);
  oneYearLaterDate.setFullYear(currentDate.getFullYear() + 3);
  const oneYearEarlierDate = new Date(currentDate);
  oneYearEarlierDate.setFullYear(currentDate.getFullYear() - 3);
  return (
    <>
      <div className="calendar-container">
        <ColoredCalendar
          defaultValue={duringDate}
          selectRange={true}
          maxDate={oneYearLaterDate} // 오늘 이후 날짜는 허용되지 않음
          minDate={oneYearEarlierDate}
        />
      </div>
    </>
  );
}

export default MyCalendar;
