import { useState, useEffect } from "react";
import { fetchShowTagsId, fetchTagName } from "../../lib/api/Api";

function FetchShowTags(id) {
  const [tagIds, setTagIds] = useState(null);
  const [tags, setTags] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTagsIds = async () => {
      try {
        setError(null);
        const ids = await fetchShowTagsId(id);
        const sortedIds = [
          ...ids
            .filter((item) => item.tag_id === 52)
            .map((item) => item.tag_id),
          ...ids
            .filter((item) => item.tag_id !== 52)
            .map((item) => item.tag_id),
        ];
        setTagIds(sortedIds);
      } catch (e) {
        setError(e);
      }
    };
    fetchTagsIds();
  }, [id]);

  useEffect(() => {
    const fetchTags = async () => {
      try {
        setError(null);
        const fetchedTags = await Promise.all(
          tagIds.map(async (tagId) => {
            return fetchTagName(tagId);
          })
        );
        setTags(fetchedTags);
      } catch (e) {
        setError(e);
      }
    };

    if (tagIds && tagIds.length > 0) {
      fetchTags()
        .then(() => setLoading(false))
        .catch((error) => setError(error));
    }
  }, [tagIds]);

  if (loading) {
    return <div>Loading tags...</div>;
  }

  if (error) {
    return <div>Error fetching tags: {error.message}</div>;
  }

  return (
    <div>
      {tags.map((tag, index) => (
        <span key={index}>{tag}</span>
      ))}
    </div>
  );
}

export default FetchShowTags;
