import React from "react";
import { useState } from "react";
import { useMap, MapMarker } from "react-kakao-maps-sdk";
import icon from "../../lib/icon/loc7.png";

const MarkEventContainer = ({ title, position, content }) => {
  const map = useMap();
  const [isVisible, setIsVisible] = useState(false);

  return (
    <MapMarker>
      key={title}
      position={position}
      onClick={() => setIsOpen(true)}
      image=
      {{
        src: icon,
        size: {
          width: 33,
          height: 33,
        }, // 마커이미지의 크기입니다
      }}
      title={position.title} // 마커의 타이틀, 마커에 마우스를 올리면 타이틀이
      표시됩니다 >
      {/* MapMarker의 자식을 넣어줌으로 해당 자식이 InfoWindow로 만들어지게 합니다 */}
      {/* 인포윈도우에 표출될 내용으로 HTML 문자열이나 React Component가 가능합니다 */}
      {isOpen && (
        <div style={{ minWidth: "150px" }}>
          <img
            alt="close"
            width="14"
            height="13"
            src="https://t1.daumcdn.net/localimg/localimages/07/mapjsapi/2x/bt_close.gif"
            style={{
              position: "absolute",
              right: "5px",
              top: "5px",
              cursor: "pointer",
            }}
          />
          <div style={{ padding: "5px", color: "#000" }}>{position.title}</div>
        </div>
      )}
    </MapMarker>
  );
};

export default React.memo(MarkEventContainer);
