/*global kakao*/
import React, { useEffect, useState } from "react";
import styled from "styled-components";
import Marker from "./Marker";
import { Map, MapMarker, CustomOverlayMap, useMap } from "react-kakao-maps-sdk";
import icon from "../../lib/icon/loc7.png";

const InfoBox = styled.div`
  width: 200px;
  height: 200px;
  background: white;
  z-index: 30;
`;

function KakaoMap({ state, flag }) {
  const mapHeight = window.innerWidth >= 560 ? 560 : window.innerWidth;
  const center = state;
  const marker = Marker();
  console.log(typeof marker, marker);

  const EventMarkerContainer = ({ position }) => {
    const [isVisible, setIsVisible] = useState(false);
    const map = useMap();
    return (
      <MapMarker
        // key={`${position.title}`}
        position={position.latlng} // 마커를 표시할 위치
        onClick={() => {
          setIsVisible(true);
        }}
        image={{
          src: icon,
          size: {
            width: 33,
            height: 33,
          }, // 마커이미지의 크기
        }}
      >
        {isVisible && (
          <CustomOverlayMap position={position.latlng} yAnchor={1.15}>
            <InfoBox></InfoBox>
          </CustomOverlayMap>
        )}
      </MapMarker>
    );
  };

  return (
    <div>
      <Map // 지도를 표시할 Container
        center={center}
        style={{
          // 지도의 크기
          width: "100%",
          height: `${mapHeight}px`,
        }}
        level={3} // 지도의 확대 레벨
      >
        {flag && <MapMarker position={center} />}
        {marker.map((position, index) => (
          <EventMarkerContainer key={index} position={position} />
        ))}
      </Map>
    </div>
  );
}

export default React.memo(KakaoMap);
