/* eslint-disable jsx-a11y/anchor-is-valid */
import * as React from "react";
import AspectRatio from "@mui/joy/AspectRatio";
import Avatar from "@mui/joy/Avatar";
import Box from "@mui/joy/Box";
import Card from "@mui/joy/Card";
import CardOverflow from "@mui/joy/CardOverflow";
import Typography from "@mui/joy/Typography";

export default function Detail() {
  //const imgSrc = "/upload/" + show_place_eng + "/seoul/" + show_name + "/1.png";
  const imgSrc = "/upload/mmca/seoul/감각의 공간, 워치 앤 칠 2.0/1.png";
  return (
    <Card
      sx={{
        "--Card-radius": (theme) => theme.vars.radius.xs,
        bgcolor: "#ffffff",
      }}
    >
      <CardOverflow>
        <Box
          component="img"
          sx={{
            width: "100%",
            margin: "auto", //박스를 쓰지말고 img 태그를 사용하면서 alt, width, height 설정해주고
          }}
          src={imgSrc}
        />
      </CardOverflow>
      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          mx: -1,
          my: 1,
        }}
      >
        <Box
          sx={{
            display: "flex",
            alignItems: "center",
            gap: 0.5,
            mx: "auto",
          }}
        >
          {[...Array(5)].map((_, index) => (
            <Box
              key={index}
              sx={{
                borderRadius: "50%",
                width: `max(${6 - index}px, 3px)`,
                height: `max(${6 - index}px, 3px)`,
                bgcolor: index === 0 ? "primary.solidBg" : "background.level3",
              }}
            />
          ))}
        </Box>
      </Box>
      <Box sx={{ display: "flex", alignItems: "center", pb: 1.5, gap: 1 }}>
        <Box
          sx={{
            position: "relative",
            "&:before": {
              content: '""',
              position: "absolute",
              top: 0,
              left: 0,
              bottom: 0,
              right: 0,
              m: "-2px",
              borderRadius: "50%",
              background:
                "linear-gradient(45deg, #f09433 0%,#e6683c 25%,#dc2743 50%,#cc2366 75%,#bc1888 100%)",
            },
          }}
        >
          <Avatar
            size="sm"
            src="/static/logo.png"
            sx={{ p: 0.5, border: "2px solid", borderColor: "background.body" }}
          />
        </Box>
        <Typography fontWeight="lg">MUI</Typography>
      </Box>
      <Typography fontSize="sm">
        {[...new Array(50)]
          .map(() => `a b c d e f g h i j k l m n o p q r s t u v w x y z.`)
          .join("\n")}
      </Typography>
    </Card>
  );
}
