import React, { useState } from "react";
import Button from "@mui/material/Button";
import ButtonGroup from "@mui/material/ButtonGroup";
import Box from "@mui/material/Box";
import { createTheme } from "@mui/material/styles";
import Search from "../items/Search";
import HashTag from "../items/HashTag";
import Map from "../items/Map";

const theme = createTheme({
  status: {
    danger: "#e53e3e",
  },
  palette: {
    primary: {
      main: "#0971f1",
      darker: "#053e85",
    },
    neutral: {
      main: "#64748B",
      contrastText: "#fff",
    },
  },
});

function MainButton() {
  const [stat, changeStat] = useState(0);

  const activateTextField = () => {
    changeStat(0);
  };
  const activateHashTag = () => {
    changeStat(1);
  };
  const activateMap = () => {
    changeStat(2);
  };

  return (
    <div>
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          "& > *": {
            m: 2,
          },
        }}
      >
        <ButtonGroup
          size="large"
          variant="text"
          aria-label="text button group"
          color="warning"
          sx={{ paddingBottom: "20px" }}
        >
          <Button onClick={activateTextField}>검색</Button>
          <Button onClick={activateHashTag}>해쉬태그</Button>
          <Button onClick={activateMap}>지도</Button>
        </ButtonGroup>
      </Box>
      <div>{stat === 0 && <Search />}</div>
      {/* 

      <div>{stat === 1 && <HashTag />} </div>
      <div>{stat === 2 && <Map />}</div> */}
    </div>
  );
}

export default MainButton;
