import { useState, useEffect } from "react";
import { database } from "./Tags";
import styled from "styled-components";
import axios from "axios";
import colorSet from "../../lib/styles/colorSet";
import TagsCheckBox from "./TagsCheckBox";
import HorizontalLine from "../common/HorizontalLine";
import PostTemplate from "../post/PostTemplate";

function IdToShow({ TagItems }) {
  const [tagIdsUrl, setTagIdsUrl] = useState("");
  const [showsId, setShowsId] = useState(null);
  const [error, setError] = useState(null);
  useEffect(() => {
    const tagObject = TagItems.reduce((acc, tagName) => {
      if (database.hasOwnProperty(tagName)) {
        acc[tagName] = database[tagName];
      }
      return acc;
    }, {});
    const tagIds = Object.values(tagObject).filter(
      (value) => typeof value === "number"
    );
    const tagIdsString = tagIds.join("&");
    tagIds.sort((a, b) => a - b);
    console.log(tagIds);
    const url = tagIdsString
      ? `/api/exhibition_showId/${tagIdsString}`
      : "/api/shows";

    const fetchShowsId = async () => {
      try {
        setError(null);
        setShowsId(null);
        // const response = await axios.get(`https://exhi-lover.com${tagIdsUrl}`);
        const response = await axios.get(
          `http://localhost:5100/api/exhibition_showId/3`
        );
        console.log(response.data);
        setShowsId(response.data);
      } catch (e) {
        setError(e);
      }
    };
    fetchShowsId();
    setTagIdsUrl(url);
  }, [TagItems]);

  return tagIdsUrl;
}

export default IdToShow;
