import React from "react";
import { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";
import colorSet from "../../lib/styles/colorSet";
import TagsCheckBox from "./TagsCheckBox";
import HorizontalLine from "../common/HorizontalLine";
import PostTemplate from "../post/PostTemplate";

function IdToShow({ TagItems }) {
  const [shows, setShows] = useState(null);
  const [tags, setTags] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const tagIdString = TagItems.join("&");
    const url = tagIdString
      ? `/api/exhibition_tags/tags/${tagIdString}`
      : "/api/shows";
    const fetchShows = async () => {
      try {
        setError(null);
        // const getShowIdResponse = await axios.get(`https://exhi-lover.com${url}`);
        const getShowIdResponse = await axios.get(
          `http://localhost:5100${url}`
        );
        const showIds = getShowIdResponse.data
          .map((i) => parseInt(i.exhibition_id))
          .sort((a, b) => a - b)
          .join("&");
        // const getShowResponse = await axios.get(
        //   `https://exhi-lover.com/api/shows/id/${showIds}`
        // );
        const getShowResponse = await axios.get(
          `http://localhost:5100/api/shows/id/${showIds}`
        );
        setShows(getShowResponse.data);
      } catch (e) {
        setError(e);
      }
    };
    if (url !== "/api/shows") {
      fetchShows();
    }
  }, [TagItems]);

  return { shows };
}

export default IdToShow;
