import * as React from "react";
import styled from "styled-components";
import palette from "../../lib/styles/palette";
import { useState } from "react";
import Link from "@mui/joy/Link";
import { Button } from "@mui/material";

const PostBlock = styled.div`
  padding: 5px 5px 30px 5px;
  width: 100%;
  max-width: 26rem;
  margin: 0 auto;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
`;

const HoverEffect = styled.div`
  background: #131313;

  &:hover {
    background: #ec6c03;
    text-decoration: underline;
    h1 {
      color: black;
    }
    h2 {
      color: black;
    }
    h3 {
      color: black;
    }
    h4 {
      color: black;
    }
    h5 {
      color: black;
    }
    strong {
      color: black;
    }
    img {
      transform: scale(1.05);
    }
  }
`;
const ContextBox = styled.div`
  box-sizing: border-box;
  padding: 13px 13px 0px 13px;
  width: 20rem;
  max-width: 23rem;
  max-height: 25rem;
  margin: 9px;
  display: flex;
  flex: 1 0 auto;
  flex-direction: column;
`;

const PostTitle = styled.div`
  text-align: left;
  color: white;
  strong {
    color: #ec6c03;
  }
  font-family: "Happiness-Sans-Title", sans-serif;
`;

const PostContent = styled.div`
  text-align: left;
  font-family: "Happiness-Sans-Title", sans-serif;
  h1 {
    color: white;
  }
  h2 {
    color: white;
  }
  h3 {
    color: white;
  }
  h4 {
    color: white;
  }
  h5 {
    color: lightgray;
  }
`;

const ImageBlock = styled.div`
  max-width: 23rem;
  margin: 0 auto;
  max-height: 25rem;
  width: auto;

  overflow: hidden;
  img {
    max-height: initial;
    transition: transform 0.5s ease-in-out;
  }
`;

const Post = ({ show, key }) => {
  console.log(show);
  const {
    show_name,
    show_artist,
    show_search,
    show_term,
    show_num,
    show_city,
    show_place,
    show_place_eng,
    show_place_detail,
    show_price,
    show_link,
    show_hashtag,
    show_imgs,
  } = show;
  const imgSrc = "/upload/seoul/" + show_place_eng + "/" + show_name + "/3.png";
  const [open, setOpen] = useState(false);
  const handleClose = () => {
    setOpen(false);
  };
  const handleClickOpen = () => {
    setOpen(true);
  };
  return (
    <>
      <PostBlock>
        <HoverEffect>
          <ContextBox>
            <PostTitle>
              <h1>
                {show_place}
                <br />
                <strong>{show_name}</strong>
              </h1>
            </PostTitle>
            <PostContent>
              <h4>{show_term}</h4>
              <h5>#태그1 #태그2 #태그3 #태그4</h5>
            </PostContent>
          </ContextBox>

          <ImageBlock>
            <img src={imgSrc} alt="" loading="lazy" />
          </ImageBlock>
        </HoverEffect>
      </PostBlock>
    </>
  );
};

export default Post;
