/*global kakao*/
import React, { useEffect, useState } from "react";
import Marker from "./Marker";
import { Map, MapMarker, CustomOverlayMap } from "react-kakao-maps-sdk";
import icon from "../../lib/icon/loc7.png";

function KakaoMap({ state, flag }) {
  const mapHeight = window.innerWidth >= 560 ? 560 : window.innerWidth;
  const center = state;
  const marker = Marker();
  console.log(typeof marker, marker);

  const EventMarkerContainer = ({ position }) => {
    const [isVisible, setIsVisible] = useState(false);
    return (
      <MapMarker
        key={`${position.title}-${position.latlng}`}
        position={position.latlng} // 마커를 표시할 위치
        onClick={() => setIsVisible(true)}
        image={{
          src: icon,
          size: {
            width: 33,
            height: 33,
          }, // 마커이미지의 크기입니다
        }}
      >
        {isVisible && (
          <CustomOverlayMap position={position.latlng}>
            <div className="wrap" style={{ minWidth: "190px" }}>
              <div className="info">
                <div className="title">
                  카카오 스페이스닷원
                  <div
                    className="close"
                    onClick={() => setIsVisible(false)}
                    title="닫기"
                  ></div>
                </div>
                <div className="body">
                  <div className="img">
                    <img
                      src="//t1.daumcdn.net/thumb/C84x76/?fname=http://t1.daumcdn.net/cfile/2170353A51B82DE005"
                      width="73"
                      height="70"
                      alt="카카오 스페이스닷원"
                    />
                  </div>
                  <div className="desc">
                    <div className="ellipsis">
                      제주특별자치도 제주시 첨단로 242
                    </div>
                    <div className="jibun ellipsis">
                      (우) 63309 (지번) 영평동 2181
                    </div>
                    <div>
                      <a
                        href="https://www.kakaocorp.com/main"
                        target="_blank"
                        className="link"
                        rel="noreferrer"
                      >
                        홈페이지
                      </a>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </CustomOverlayMap>
          // <div style={{ minWidth: "190px" }}>
          //   <div style={{ padding: "5px", color: "#000" }}>
          //     {position.title}
          //     <img
          //       alt="close"
          //       width="14"
          //       height="13"
          //       src="https://t1.daumcdn.net/localimg/localimages/07/mapjsapi/2x/bt_close.gif"
          //       style={{
          //         position: "absolute",
          //         right: "5px",
          //         top: "5px",
          //         cursor: "pointer",
          //       }}
          //       onClick={() => setIsVisible(false)}
          //     />
          //   </div>
          // </div>
        )}
      </MapMarker>
    );
  };

  return (
    <div>
      <Map // 지도를 표시할 Container
        center={center}
        style={{
          // 지도의 크기
          width: "100%",
          height: `${mapHeight}px`,
        }}
        level={3} // 지도의 확대 레벨
      >
        {flag && <MapMarker position={center} />}
        {marker.map((position, index) => (
          <EventMarkerContainer key={index} position={position} />
          // <MapMarker
          //   key={`${position.title}-${position.latlng}`}
          //   position={position.latlng} // 마커를 표시할 위치
          //   clickable={true}
          //   onClick={() => setIsOpen(true)}
          //   image={{
          //     src: icon,
          //     size: {
          //       width: 33,
          //       height: 33,
          //     }, // 마커이미지의 크기입니다
          //   }}
          //   title={position.title} // 마커의 타이틀, 마커에 마우스를 올리면 타이틀이 표시됩니다
          // >
          //   {/* MapMarker의 자식을 넣어줌으로 해당 자식이 InfoWindow로 만들어지게 합니다 */}
          //   {/* 인포윈도우에 표출될 내용으로 HTML 문자열이나 React Component가 가능합니다 */}
          //   {isOpen && (
          //     <div style={{ minWidth: "180px" }}>
          //       <div style={{ padding: "5px", color: "#000" }}>
          //         {position.title}
          //       </div>
          //     </div>
          //   )}
          // </MapMarker>
        ))}
      </Map>
    </div>
  );
}

export default React.memo(KakaoMap);
