      ? `/api/exhibition_showId/${tagIdsString}`
      : "/api/shows";

    const fetchShowsId = async () => {
      try {
        setError(null);
        setShowsId(null);
        if (tagIdsUrl !== "") {
          // const response = await axios.get(`https://exhi-lover.com${tagIdsUrl}`);
          const response = await axios.get(`http://localhost:5100${tagIdsUrl}`);
          setShowsId(response.data);
        }
      } catch (e) {
        setError(e);
      }
    };
    const fetchShow = async () => {
      try {
      } catch (e) {}
    };

    setTagIdsUrl(url);
    fetchShowsId();
  }, [TagItems, tagIdsUrl]);

  return showsId, null;
}

export default IdToShow;
