import AspectRatio from "@mui/joy/AspectRatio";
import Avatar from "@mui/joy/Avatar";
import Box from "@mui/joy/Box";
import Card from "@mui/joy/Card";
import CardCover from "@mui/joy/CardCover";
import Typography from "@mui/joy/Typography";
import Link from "@mui/joy/Link";

function Post() {
  const imageStyle = {
    height: "auto",
    width: "35ch",
  };
  return (
    <Card
      sx={{
        //minWidth: 150,
        width: "40ch",
        height: "auto",
        bgcolor: "initial", //
        boxShadow: "none",
        margin: "auto",
        "--Card-padding": "10px",
        // post.js 영역 넓이
      }}
    >
      <Box
        sx={{
          position: "relative",
          width: "40ch", //이미지, 제목 영역
        }}
      >
        <img
          src="/upload/mmca/seoul/감각의 공간, 워치 앤 칠 2.0/1.png"
          style={imageStyle}
        />
        <CardCover
          className="gradient-cover"
          sx={{
            "&:hover, &:focus-within": {
              opacity: 1,
            },
            //opacity: 0, 이걸 넣으면 마우스를 올리거나 클리하면 생기고 때면 사라짐. 즉 나한테 필요 없뜸
            transition: "0.1s ease-in",
            paddingRight: "10px",

            color: "#ffffff",
            background:
              "linear-gradient(180deg, transparent 62%, rgba(0,0,0,0.00345888) 63.94%, rgba(0,0,0,0.014204) 65.89%, rgba(0,0,0,0.0326639) 67.83%, rgba(0,0,0,0.0589645) 69.78%, rgba(0,0,0,0.0927099) 71.72%, rgba(0,0,0,0.132754) 73.67%, rgba(0,0,0,0.177076) 75.61%, rgba(0,0,0,0.222924) 77.56%, rgba(0,0,0,0.267246) 79.5%, rgba(0,0,0,0.30729) 81.44%, rgba(0,0,0,0.341035) 83.39%, rgba(0,0,0,0.367336) 85.33%, rgba(0,0,0,0.385796) 87.28%, rgba(0,0,0,0.396541) 89.22%, rgba(0,0,0,0.4) 91.17%)",
          }}
        >
          {/* The first box acts as a container that inherits style from the CardCover */}
          <Box>
            {/*shows_name 크기 */}
            <Box
              sx={{
                p: 2,
                display: "flex",
                alignItems: "center",
                gap: 1.5,
                flexGrow: 1,
                alignSelf: "flex-end",
                width: "20ch",
              }}
            >
              <Typography level="h2" noWrap sx={{ fontSize: "lg" }}>
                <Link
                  href="#dribbble-shot"
                  overlay
                  underline="none"
                  sx={{
                    color: "#fff",
                    textOverflow: "ellipsis",
                    overflow: "hidden",
                    display: "block",
                  }}
                >
                  shows_name
                </Link>
              </Typography>
            </Box>
          </Box>
        </CardCover>
      </Box>
      <Box
        sx={{
          display: "flex",
          gap: 1,
          mt: 1.5,
          alignItems: "center",
          paddingBottom: "10px",
        }}
      >
        <Avatar
          src="https://images.unsplash.com/profile-1502669002421-a8d274ad2897?dpr=2&auto=format&fit=crop&w=32&h=32&q=60&crop=faces&bg=fff"
          size="sm"
          sx={{ "--Avatar-size": "1.5rem" }}
        />
        <Typography
          sx={{
            fontSize: "sm",
            fontWeight: "md",
            color: "white",
          }}
        >
          Exhibitor
        </Typography>
      </Box>
    </Card>
  );
}

export default Post;
