import { useState, useEffect } from "react";
import axios from "axios";
import {
  fetchAllShows,
  fetchShowByShowId,
  fetchShowIdByTagId,
} from "../../lib/api/api";

function IdToShow({ TagItems }) {
  const [shows, setShows] = useState(null);
  const [tags, setTags] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const tagIdString = TagItems.join("&");
    const fetchShows = async () => {
      if (tagIdString !== "") {
        try {
          setError(null);
          const showsIds = await fetchShowIdByTagId(tagIdString);
          const tagsId = await axios.get(
            `http://localhost:5100/api/exhibition_tags/shows/${showsIds}`
          );
          setTags(tagsId);
          const getShowResponse = await fetchShowByShowId(showsIds);
          setShows(getShowResponse);
        } catch (e) {
          setError(e);
        }
      } else {
        const show = await fetchAllShows();
        const a = show.map((show) => show.id).join("&");
        const tagsId = await axios.get(
          `http://localhost:5100/api/exhibition_tags/shows/${a}`
        );
        setShows(show);
        setTags(tagsId);
      }
    };

    fetchShows();
  }, [TagItems]);

  return { shows, tags };
}

export default IdToShow;
