import * as React from "react";
import { useState } from "react";
import styled from "styled-components";

import { era, genre, takePhoto, artMovement, orient } from "./Tags";
import TagsCheckBox from "./TagsCheckBox";
import HorizontalLine from "../common/HorizontalLine";

const Box = styled.div`
  algin-item: left;
  width: 95%;
  padding: 0.4rem 0 0 1.1rem;
  margin: 0 auto;
  padding: 5px 5px 30px 5px;
`;

function HashTag() {
  const [checkedItems, setCheckedItems] = useState([]);
  console.log(checkedItems);
  return (
    <>
      <Box>
        <HorizontalLine />
        <TagsCheckBox
          title={"촬영 가능 여부"}
          items={takePhoto}
          handleCheck={setCheckedItems}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"지역별"}
          items={orient}
          handleCheck={setCheckedItems}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"장르별"}
          items={genre}
          handleCheck={setCheckedItems}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"시대별"}
          items={era}
          handleCheck={setCheckedItems}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"미술사조"}
          items={artMovement}
          handleCheck={setCheckedItems}
        />
        <HorizontalLine />
      </Box>
    </>
  );
}

export default HashTag;
