import * as React from "react";
import { useState } from "react";
import PropTypes from "prop-types";
import dayjs from "dayjs";
import isBetweenPlugin from "dayjs/plugin/isBetween";
import { styled } from "@mui/material/styles";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DateCalendar } from "@mui/x-date-pickers/DateCalendar";
import { PickersDay } from "@mui/x-date-pickers/PickersDay";
import { createTheme } from "@mui/material/styles";
import { ThemeProvider } from "@mui/material/styles";

const theme = createTheme({
  palette: {
    primary: {
      main: "#ffffff",
    },
  },
});

dayjs.extend(isBetweenPlugin);

const CustomPickersDay = styled(PickersDay, {
  shouldForwardProp: (prop) =>
    prop !== "dayIsBetween" && prop !== "isFirstDay" && prop !== "isLastDay",
})(({ dayIsBetween, isFirstDay, isLastDay, Today }) => ({
  ...(dayIsBetween && {
    borderRadius: "0%",
    backgroundColor: "#cacaca",
    color: "#353535",
    "&:hover, &:focus": {
      backgroundColor: "#cacaca",
    },
  }),
  ...(isFirstDay && {
    borderTopLeftRadius: "50%",
    borderBottomLeftRadius: "50%",
    backgroundColor: "#cacaca",
    color: "#000000",
  }),
  ...(isLastDay && {
    borderTopRightRadius: "50%",
    borderBottomRightRadius: "50%",
    backgroundColor: "#cacaca",
    color: "#000000",
  }),
  ...(isToday && {
    borderTopRightRadius: "50%",
    borderBottomRightRadius: "50%",
    backgroundColor: "#cccccc",
    color: "#000000",
  }),
}));

function Day(props) {
  const { day, startDay, endDay, today, ...other } = props;

  if (startDay == null || endDay == null) {
    return <PickersDay day={day} {...other} />;
  }
  console.log(today);
  console.log(startDay);
  const dayIsBetween = day.isBetween(startDay, endDay, null, "[]");
  const isFirstDay = day.isSame(startDay, "day");
  const isLastDay = day.isSame(endDay, "day");
  const Today = day.isSame(today, "day");

  return (
    <CustomPickersDay
      {...other}
      day={day}
      disableMargin
      dayIsBetween={dayIsBetween}
      isFirstDay={isFirstDay}
      isLastDay={isLastDay}
      Today={Today}
    />
  );
}

Day.propTypes = {
  day: PropTypes.object.isRequired,
  selectedDay: PropTypes.object,
};

function MyCalendar({ date }) {
  const dateArray = date.split(" ~ ");
  const today = dayjs(dayjs().format("YYYY-MM-DD"));
  const [startDay, setStartDay] = useState(dayjs(dateArray[0]));
  const [endDay, setEndDay] = useState(dayjs(dateArray[1]));
  // console.log(today);
  // console.log(endDay);
  return (
    <LocalizationProvider dateAdapter={AdapterDayjs}>
      <ThemeProvider theme={theme}>
        <DateCalendar
          value={today}
          slots={{ day: Day }}
          slotProps={{
            day: {
              startDay: startDay,
              endDay: endDay,
              today: today,
            },
          }}
          sx={{
            width: "100%",
            height: "auto",
            color: "#000000",
            backgroundColor: "#ffffff",
          }}
          showDaysOutsideCurrentMonth
          fixedWeekNumber={5}
          readOnly
        />
      </ThemeProvider>
    </LocalizationProvider>
  );
}

export default React.memo(MyCalendar);
