import * as React from "react";
import { styled } from "@mui/material/styles";
import Box from "@mui/material/Box";
import ButtonBase from "@mui/material/ButtonBase";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";

const images = [
  {
    url: "/upload/seoul/mmca/감각의 공간, 워치 앤 칠 2.0/1.png",
    title: "1",
    height: "350px",
    width: "100%",
  },
  {
    url: "/upload/seoul/mmca/감각의 공간, 워치 앤 칠 2.0/2.png",
    title: "2",
    height: "350px",
    width: "100%",
  },
  {
    url: "/upload/seoul/mmca/감각의 공간, 워치 앤 칠 2.0/3.png",
    title: "3",
    height: "350px",
    width: "100%",
  },
];

const ImageButton = styled(ButtonBase)(({ theme }) => ({
  position: "relative",
  height: 350,
  [theme.breakpoints.down("sm")]: {
    //"100% !important", // Overrides inline-style
    height: 350,
  },
  "&:hover, &.Mui-focusVisible": {
    zIndex: 1,
    "& .MuiImageBackdrop-root": {
      opacity: 0.15,
    },
    "& .MuiImageMarked-root": {
      opacity: 0,
    },
    "& .MuiTypography-root": {
      border: "4px solid currentColor",
    },
  },
}));

export default function ImageList() {
  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "row",
        overflow: "hidden",
        overflowX: "scroll",
        height: "350px",
        marginTop: "15px",
        marginBottom: "15px",
      }}
    >
      {images.map((i) => (
        <ImageButton
          focusRipple
          key={i.title}
          style={{
            height: 350,
          }}
        >
          <Box
            component="img"
            sx={{
              height: "100%",
              margin: "auto", //박스를 쓰지말고 img 태그를 사용하면서 alt, width, height 설정해주고
            }}
            src={i.url}
          />
        </ImageButton>
      ))}
    </Box>
  );
}
