/*global kakao*/
import React, {
  useEffect,
  useRef,
  useState,
  useCallback,
  useMemo,
} from "react";
import icon from "../../lib/icon/loc.png";
import iconDark from "../../lib/icon/dot.png";
import SwipeableEdgeDrawer from "./SwipeableEdgeDrawer";

function KakaoMap({ lat, lng, flag, galleriesMarker }) {
  const mapHeight = (window.innerWidth >= 560 ? 560 : window.innerWidth) * 1.1;
  const mapContainer = useRef(null);

  const [selectedMarker, setSelectedMarker] = useState(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [lastestClickedMarker, setLastestClickedMarker] = useState(null);

  const imageSize = useMemo(() => new window.kakao.maps.Size(35, 35), []);
  const darkImageSize = useMemo(() => new window.kakao.maps.Size(16, 16), []);
  const galleryMarkerImage = useMemo(
    () => new window.kakao.maps.MarkerImage(icon, imageSize),
    [imageSize]
  );
  const galleryMarkerImageDark = useMemo(
    () => new window.kakao.maps.MarkerImage(iconDark, darkImageSize),
    [darkImageSize]
  );

  const mapOptions = useMemo(
    () => ({
      center: new window.kakao.maps.LatLng(lat, lng),
      level: 6,
    }),
    [lat, lng]
  );

  const createGalleryMarker = useCallback((galleryMarker, map, markerImage) => {
    const setGalleryMarker = new window.kakao.maps.Marker({
      map: map,
      position: new window.kakao.maps.LatLng(
        galleryMarker.latlng.lat,
        galleryMarker.latlng.lng
      ),
      image: markerImage,
      zIndex: galleryMarker.onDisplay ? 99 : undefined,
    });

    window.kakao.maps.event.addListener(setGalleryMarker, "click", () => {
      map.panTo(setGalleryMarker.getPosition());
      window.scrollTo({
        top: document.body.scrollHeight,
        behavior: "smooth",
      });

      setSelectedMarker(galleryMarker);
      setLastestClickedMarker(setGalleryMarker.getPosition());
      localStorage.setItem(
        "lastClickedMarker",
        JSON.stringify({
          lat: setGalleryMarker.getPosition().getLat(),
          lng: setGalleryMarker.getPosition().getLng(),
        })
      );
      setDrawerOpen(true);
    });
  }, []);

  useEffect(() => {
    const map = new window.kakao.maps.Map(mapContainer.current, mapOptions);

    const storedLastClickedMarker = localStorage.getItem("lastClickedMarker");
    const initialLastClickedMarker = storedLastClickedMarker
      ? JSON.parse(storedLastClickedMarker)
      : null;

    setLastestClickedMarker(
      initialLastClickedMarker
        ? new window.kakao.maps.LatLng(
            initialLastClickedMarker.lat,
            initialLastClickedMarker.lng
          )
        : null
    );

    if (flag) {
      const markerPosition = new window.kakao.maps.LatLng(lat, lng);
      const marker = new window.kakao.maps.Marker({
        map: map,
        position: markerPosition,
      });
    }

    if (lastestClickedMarker) {
      map.panTo(lastestClickedMarker);
    }

    galleriesMarker.forEach((galleryMarker) => {
      if (galleryMarker.onDisplay) {
        createGalleryMarker(galleryMarker, map, galleryMarkerImage);
      } else {
        createGalleryMarker(galleryMarker, map, galleryMarkerImageDark);
      }
    });
  }, [
    flag,
    galleriesMarker,
    lastestClickedMarker,
    createGalleryMarker,
    galleryMarkerImage,
    galleryMarkerImageDark,
    mapOptions,
  ]);

  return (
    <div>
      <div
        style={{
          width: `100%`,
          maxWidth: "560px",
          height: `${mapHeight + 50}px`,
          position: "absolute",
        }}
      >
        <div id="map" ref={mapContainer} className="map-container" />
        {selectedMarker && drawerOpen ? (
          <SwipeableEdgeDrawer
            gallery={selectedMarker}
            onClose={() => {
              setDrawerOpen(false);
            }}
          />
        ) : (
          <SwipeableEdgeDrawer />
        )}
      </div>
    </div>
  );
}

export default KakaoMap;
