import styled from "styled-components";
import GlobalStyle from "../../lib/styles/fontStyle";

const DetailViewerBlock = styled.div`
  width: 100%;
`;

const MainImage = styled.img`
  width: 100%;
  aspect-ratio: 16/9; /* change to the desired aspect ratio */
  object-fit: cover;
  overflow: hidden;
  display: flex;
  align-items: center;
  border-bottom: 0.03rem solid #202020;
`;

const MainTitle = styled.div`
  text-align: left;
  color: white;
  padding-left: 0.8rem;
  padding-top: 1.2rem;
  p {
    font-size: 1.7rem;
    font-family: San Francisco, "Happiness-Sans-Bold", sans-serif;
    margin: 0.4rem 0;
  }
  strong {
    font-size: 1.8rem;
    color: ${(props) => props.color || "#ec6c03"};
    font-family: San Francisco, "Happiness-Sans-Title", sans-serif;
  }
`;

const Booking = styled.div``;

const Info = styled.div``;

const Tags = styled.div``;

const DetailViewer = ({ show, color }) => {
  const {
    show_name,
    show_artist,
    show_search,
    show_term,
    show_num,
    show_city,
    show_place,
    show_place_eng,
    show_place_detail,
    show_price,
    show_link,
    show_hashtag,
    show_imgs,
  } = show[0];
  console.log({ color });
  const imgSrc = "/upload/seoul/" + show_place_eng + "/" + show_name + "/1.png";
  return (
    <>
      <GlobalStyle />
      <DetailViewerBlock>
        <MainImage src={imgSrc} alt="" loading="lazy" />
        <MainTitle color={color}>
          <p>{show_place}</p>
          <strong>{show_name}</strong>
        </MainTitle>
        <Booking></Booking>
        <Info>{show_name}</Info>
        <Tags>
          <div className="tag">#tag1</div>
          <div className="tag">#tag2</div>
          <div className="tag">#tag3</div>
        </Tags>
      </DetailViewerBlock>
    </>
  );
};

export default DetailViewer;
