import * as React from "react";
import { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";
import colorSet from "../../lib/styles/colorSet";
import { era, genre, takePhoto, artMovement, orient } from "./Tags";
import TagsCheckBox from "./TagsCheckBox";
import HorizontalLine from "../common/HorizontalLine";
import Post from "../post/Post";
import IdToShow from "./IdToShow";
import { fetchShowTagsId } from "../../lib/api/api";

const Box = styled.div`
  algin-item: left;
  width: 100%;
  padding: 0.4rem 0 0 1.1rem;
  margin: 0 auto;
  padding: 5px 5px 30px 5px;
`;

function HashTag() {
  const [showsId, setShowsId] = useState(null);
  const [error, setError] = useState(null);
  const [tags, setTags] = useState(null);
  const [tagsId, setTagsId] = useState(null);

  const [selectedItems, setSelectedItems] = useState([]);
  const [selectedPhoto, setSelectedPhoto] = useState([]);
  const [selectedOrient, setSelectedOrient] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState([]);
  const [selectedEra, setSelectedEra] = useState([]);
  const [selectedArtMovement, setSelectedArtMovement] = useState([]);

  const handleCheckedPhoto = (items) => {
    setSelectedPhoto(items);
  };
  const handleCheckedOrient = (items) => {
    setSelectedOrient(items);
  };
  const handleCheckedGenre = (items) => {
    setSelectedGenre(items);
  };
  const handleCheckedEra = (items) => {
    setSelectedEra(items);
  };
  const handleCheckedArtMovement = (items) => {
    setSelectedArtMovement(items);
  };

  useEffect(() => {
    setSelectedItems((prevSelectedItems) => {
      const items = [
        ...selectedPhoto,
        ...selectedOrient,
        ...selectedGenre,
        ...selectedEra,
        ...selectedArtMovement,
      ];
      const orderedItmes = items.sort((a, b) => a - b);
      return orderedItmes;
    });
  }, [
    selectedPhoto,
    selectedOrient,
    selectedGenre,
    selectedEra,
    selectedArtMovement,
  ]);

  const { shows } = IdToShow({ TagItems: selectedItems });
  useEffect(() => {
    const fetchTagsId = async () => {
      try {
        setError(null);
        const tagsIdData = await Promise.all(
          shows.map(async (show) => {
            const data = fetchShowTagsId(show.id);
            return data;
          })
        );
        if (tagsIdData.length > 0) {
          const tagsIdArray = tagsIdData.reduce((acc, innerArray) => {
            innerArray.forEach((row) => {
              const { exhibition_id, tag_id } = row;
              const index = acc.findIndex(
                (obj) => obj.exhibition_id === exhibition_id
              );
              if (index === -1) {
                acc.push({ exhibition_id, tags: [tag_id] });
              } else {
                acc[index].tags.push(tag_id);
              }
            });
            return acc;
          }, []);
          setTagsId(tagsIdArray);
        }
      } catch (e) {
        setError(e);
      }
    };

    fetchTagsId();
  }, [shows]);

  useEffect(() => {
    const fetchTag = async () => {
      try {
        setError(null);
        const tagData = await Promise.all(
          tagsId.map(async ({ exhibition_id, tags }) => {
            const tagNames = await Promise.all(
              tags.map(async (tagId) => {
                const url = `/api/tags/${tagId}`;
                const response = await axios.get(
                  // `https://exhi-lover.com${url}`
                  `http://localhost:5100${url}`
                );
                return response.data[0].tag_name;
              })
            );
            return { exhibition_id, tagNames };
          })
        );
        setTags(
          tagData.reduce((acc, { exhibition_id, tagNames }) => {
            acc[exhibition_id] = tagNames;
            return acc;
          }, {})
        );
      } catch (e) {
        setError(e);
      }
    };

    if (tagsId !== null) {
      fetchTag();
    }
  }, [tagsId]);
  useEffect(() => {});
  return (
    <>
      <Box>
        <HorizontalLine />
        <TagsCheckBox
          title={"촬영 가능 여부"}
          items={takePhoto}
          handleCheckedItems={handleCheckedPhoto}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"지역별"}
          items={orient}
          handleCheckedItems={handleCheckedOrient}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"장르별"}
          items={genre}
          handleCheckedItems={handleCheckedGenre}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"시대별"}
          items={era}
          handleCheckedItems={handleCheckedEra}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"미술사조"}
          items={artMovement}
          handleCheckedItems={handleCheckedArtMovement}
        />
        <HorizontalLine />
        {shows &&
          shows.map((show, index) => {
            const key = `${show.show_name}`; // create unique key prop value
            const colorIndex = index % colorSet.length; // determine color index based on position in array
            const color = colorSet[colorIndex]; // get color from colorSet array
            return (
              <Post
                show={show}
                key={key}
                color={color}
                tags={tags && tags[show.id]}
              />
            ); // pass color as prop to Post component
          })}
      </Box>
    </>
  );
}

export default HashTag;
