// Move the logic inside FetchShowTags into a custom hook
import { useState, useEffect } from "react";
import { fetchShowTagsId, fetchTagName } from "../../lib/api/Api";

function UseFetchShowTags(id) {
  const [tags, setTags] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTags = async () => {
      try {
        setError(null);
        const ids = await fetchShowTagsId(id);
        const sortedIds = [
          ...ids
            .filter((item) => item.tag_id === 52)
            .map((item) => item.tag_id),
          ...ids
            .filter((item) => item.tag_id !== 52)
            .map((item) => item.tag_id),
        ];
        const fetchedTags = await Promise.all(
          sortedIds.map(async (tagId) => {
            return fetchTagName(tagId);
          })
        );
        setTags(fetchedTags);
      } catch (e) {
        setError(e);
      }
    };

    fetchTags();
  }, [id]);

  return tags;
}

export default UseFetchShowTags;
