import * as React from "react";
import styled from "styled-components";
import palette from "../../lib/styles/palette";

const PostBlock = styled.div`
  padding: 10px;
  width: 100%;
  max-width: 30rem;
  margin: 0 auto;
  display: flex;
  flex-wrap: wrap;
`;
const ContextBox = styled.div`
  max-width: 23rem;
  margin: 0 auto;
  max-height: 25rem;
  padding: 10px;
  display: flex;
  flex: 1 0 auto;
  flex-direction: column;
  background: 3d3d3d;

  &:hover {
    background: #ec6c03;
    text-decoration: underline;
    h1 {
      color: black;
    }
    h3 {
      color: black;
    }
    strong {
      color: black;
    }
  }
`;

const PostTitle = styled.div`
  text-align: left;
  color: white;
  strong {
    color: #ec6c03;
  }
`;

const PostContent = styled.div`
  text-align: left;
  color: white;
`;

const ImageBlock = styled.div`
  max-width: 23rem;
  margin: 0 auto;
  max-height: 25rem;
  padding: 10px;

  overflow: hidden;
  img {
    max-height: initial;
    widht: 80vh;
  }
`;

const Post = () => {
  return (
    <>
      <PostBlock>
        <ContextBox>
          <PostTitle>
            <h1>
              sdafsdf
              <br />
              <strong>ewr</strong>
            </h1>
          </PostTitle>
          <PostContent>
            <h3>hey</h3>
          </PostContent>
        </ContextBox>

        <ImageBlock>
          <img
            src="/upload/seoul/mmca/감각의 공간, 워치 앤 칠 2.0/1.png"
            alt=""
            loading="lazy"
          />
        </ImageBlock>
      </PostBlock>
    </>
  );
};

export default Post;
