import styled from "styled-components";
import GlobalStyle from "../../lib/styles/fontStyle";
import { Link } from "react-router-dom";
import { AiFillCaretRight } from "react-icons/ai";

const DetailViewerBlock = styled.div`
  width: 100%;
  div {
    text-align: left;
  }
  Booking {
    display: flex;
    justify-content: center;
    align-items: center;
    color: black;
  }
`;

const MainImage = styled.img`
  padding-left: 0.8rem;
  width: 100%;
  aspect-ratio: 16/9; /* change to the desired aspect ratio */
  object-fit: cover;
  overflow: hidden;
  display: flex;
  align-items: center;
  border-bottom: 0.03rem solid #202020;
`;

const MainTitle = styled.div`
  padding-left: 0.8rem;
  color: white;
  padding-top: 1.2rem;
  p {
    font-size: 1.5rem;
    font-family: = "Happiness-Sans-Bold", sans-serif;
    margin: 0.4rem 0;
  }
  strong {
    font-size: 1.8rem;
    color: ${(props) => props.color || "#ec6c03"};
    font-family: = "Happiness-Sans-Title", sans-serif;
  }
`;

const Term = styled.div`
  padding-left: 0.8rem;
  color: white;
  padding-top: 1.3rem;
  font-size: 0.9rem;
`;

const BookingArea = styled.div`
  margin-top: 1rem;
`;
const Booking = styled.div`
  background: ${(props) => props.color || "#ec6c03"};
  width: 94%;
  height: 2.8rem;
  color: #010101;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: auto;
  padding-right: 2rem;
  p {
    padding-left: 1rem;
    font-size: 1.1rem;
    font-family: "Happiness-Sans-Bold";
  }
  svg {
    // margin-left: 1rem; /* add margin to the left side */
    font-size: 1.3rem;
  }
  &:hover {
    color: white;
    background: #010101;
    border: 1px solid #ffffff;
  }
`;

const Info = styled.div``;

const Tags = styled.div``;

const DetailViewer = ({ show, color }) => {
  const {
    show_name,
    show_artist,
    show_search,
    show_term,
    show_num,
    show_city,
    show_place,
    show_place_eng,
    show_place_detail,
    show_price,
    show_link,
    show_hashtag,
    show_imgs,
  } = show[0];
  const booking_site = null;
  console.log({ color });
  const imgSrc = "/upload/seoul/" + show_place_eng + "/" + show_name + "/1.png";
  return (
    <>
      <GlobalStyle />
      <DetailViewerBlock>
        <MainImage src={imgSrc} alt="" loading="lazy" />
        <MainTitle color={color}>
          <p>{show_place}</p>
          <strong>{show_name}</strong>
        </MainTitle>
        <Term>{show_term}</Term>
        {/* <Link to={booking_site}> */}
        <BookingArea>
          <Booking color={color}>
            <p>예약하기</p>
            <AiFillCaretRight />
          </Booking>
        </BookingArea>
        {/* </Link> */}
        <Info>aserewrawe</Info>
        <Tags>
          <div className="tag">#tag1</div>
          <div className="tag">#tag2</div>
          <div className="tag">#tag3</div>
        </Tags>
      </DetailViewerBlock>
    </>
  );
};

export default DetailViewer;
