import { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";
import colorSet from "../../lib/styles/colorSet";
import TagsCheckBox from "./TagsCheckBox";
import HorizontalLine from "../common/HorizontalLine";
import PostTemplate from "../post/PostTemplate";

function IdToShow({ TagItems }) {
  const [tagIdsUrl, setTagIdsUrl] = useState("");
  const [showsId, setShowsId] = useState(null);
  const [shows, setShows] = useState(null);
  const [tags, setTags] = useState(null);
  const [tagsId, setTagsId] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const tagIdsString = TagItems.join("&");
    console.log(tagIdsString);
    const url = tagIdsString
      ? `/api/exhibition_showId/${tagIdsString}`
      : "/api/shows";

    const fetchShowsId = async () => {
      try {
        setError(null);
        setShowsId(null);
        // const response = await axios.get(`https://exhi-lover.com${url}`);
        const response = await axios.get(`http://localhost:5100${url}`);
        if (url !== "/api/shows") {
          const exhibitionIds = response.data.map(
            ({ exhibition_id }) => exhibition_id
          );
          exhibitionIds.sort((a, b) => a - b);

          setShowsId(exhibitionIds);
          console.log(showsId);
          setShows(null);
        } else if (url === "/api/shows") {
          setShows(response.data);
        }
      } catch (e) {
        setError(e);
      }
    };

    fetchShowsId();
  }, [TagItems]);

  useEffect(() => {
    const fetchShow = async () => {
      if (showsId !== null) {
        try {
          setError(null);
          setShows(null);
          const showUrl = showsId ? `/api/shows_id/${showsId}` : "/api/shows";
          // const response = await axios.get(`https://exhi-lover.com${showUrl}`);
          const response = await axios.get(`http://localhost:5100${showUrl}`);
          setShows(response.data);
        } catch (e) {
          setError(e);
        }
      }
    };

    fetchShow();
  }, [showsId]);

  return { shows, tags };
}

export default IdToShow;
