import React, { useEffect, useState } from "react";
import styled from "styled-components";
import line from "../../lib/icon/line.png";
import FetchShowsInfo from "./FetchShowsInfo";
import GlobalStyle from "../../lib/styles/fontStyle";

const Drawer = styled.div`
  position: absolute;
  display: flex;
  flex-direction: column; /* Stack elements vertically */
  justify-content: flex-start; /* Stick to the top */
  align-items: center; /* Center horizontally */
  padding-top: 10px;
  padding-right: 14px;
  padding-left: 14px;
  height: ${(props) => props.height}px;
  max-height: ${(props) => props.height}px;
  //   height: 200px;
  width: 100%;
  background: #1e1e1e;
  border-radius: 15px 15px 0 0;
  bottom: 0;
  z-index: 999;
`;

const Container = styled.div`
  background: #e2e2e2;
  // justify-content: flex-start; /* Stick to the top */
  flex-direction: column; /* Stack elements vertically */
  width: 100%;
  margin: 10px 10px 3px 10px;
  padding: 10px 10px 10px 10px;
  // display: flex;
  flex: 1;
  justify-content: center;
  border-radius: 10px 10px 0 0;

  font-size: 20px;
  font-style: normal;
  font-weight: 700;
  line-height: normal;
  font-family: "Happiness-Sans-Title", sans-serif;
`;
const ScrollBox = styled.div`
  overflow-y: scroll;
  height: 150px;
`;

const Content = styled.div`
  // flex: 1;
  display: flex;
  // justify-content: center;
  flex-direction: column;
  flex-direction: row; /* Stack elements vertically */
  margin-top: 10px;

  // padding: 20px; /* Apply padding here */
  background: white;

  font-size: 18px;
  img {
    position: relative;
    top: 0;
    width: 84px;
    height: 120px;
  }
`;

const Inform = styled.div`
  //   margin-top: 10px;
`;

const SwipeableEdgeDrawer = ({ gallery }) => {
  const [currentHeight, setCurrentHeight] = useState(100);
  const galleryInfo = FetchShowsInfo(gallery?.id);
  //   const [galleryInfo, setGalleryInfo] = useState();
  useEffect(() => {
    if (gallery !== undefined) {
      setCurrentHeight(200);
      //   setGalleryInfo(FetchShowsInfo(gallery.id));
    } else {
      setCurrentHeight(20);
    }
  }, [gallery]);
  console.log(galleryInfo);
  //   console.log(galleryInfo);
  //   console.log(gallery.id);
  //   console.log(currentHeight);

  return (
    <>
      <GlobalStyle />
      <Drawer currentHeight={currentHeight}>
        <img src={line} alt="Line" style={{ width: "120px", height: "5px" }} />
        {gallery ? (
          <Container>
            {gallery.title}
            <ScrollBox>
              {galleryInfo
                ? galleryInfo.map((show, index) => {
                    const imgSrc =
                      "/upload/shows/" +
                      show.show_place_eng +
                      "/" +
                      show.id +
                      "/1.png";
                    return (
                      <Content key={index}>
                        <img src={imgSrc} alt="" />
                        <Inform>
                          {show.show_name}
                          {show.show_artist}
                          {show.show_term}
                        </Inform>
                      </Content>
                    );
                  })
                : "LOADING..."}
            </ScrollBox>
          </Container>
        ) : (
          <div></div>
        )}
      </Drawer>
    </>
  );
};

export default React.memo(SwipeableEdgeDrawer);
