import React from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Paper from "@mui/material/Paper";
import Post from "./Post";
/*
const theme = createTheme({
  palette: {
    primary: {
      main: purple[400],
    },
    secondary: {
      main: "#11cb5f",
    },
  },
});
 */

function Search() {
  return (
    <Paper
      sx={{
        backgroundColor: "#2a2a2a",
        width: "30ch",
        margin: "auto",
        paddingBottom: "10px",
        paddingTop: "10px",
      }}
    >
      <Box
        component="form"
        sx={{
          "& .MuiTextField-root": { m: 1, width: "25ch" },
          margin: "auto",
        }}
        noValidate
        autoComplete="off"
      >
        <TextField
          id="standard-search"
          label="전시회, 작가 이름 검색"
          type="search"
          variant="standard"
          color="warning"
          inputProps={{ style: { color: "#ECD3BF" } }}
          focused
        />
      </Box>
      <Post />
      {this.state.shows
        ? this.state.shows.map((c) => {
            return { c };
          })
        : ""}
    </Paper>
  );
}

export default Search;
