import { useState, useEffect } from "react";
import axios from "axios";
import { fetchShowByShowId, fetchShowIdByTagId } from "../../lib/api/api";

function IdToShow({ TagItems }) {
  const [shows, setShows] = useState(null);
  const [tagIdString, setTagIdString] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    setTagIdString(TagItems.join("&"));
    console.log(tagIdString);
    const fetchShows = async () => {
      if (tagIdString !== null) {
        try {
          setError(null);
          const showIds = await fetchShowIdByTagId(tagIdString);
          const getShowResponse = await fetchShowByShowId(showIds);
          setShows(getShowResponse);
        } catch (e) {
          setError(e);
        }
      } else {
        const show = await fetchShows();
        setShows();
      }
    };

    fetchShows();
  }, [TagItems]);

  return { shows };
}

export default IdToShow;
