import React, { useState, useEffect, useRef, useCallback } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";
import axios from "axios";
import Post from "../post/Post";
import PostTemplate from "../post/PostTemplate";
import SearchBar from "../search/SearchBar";
import styled from "styled-components";
import DetailTemplate from "../detail/DetailTemplate";
import HorizontalLine from "../common/HorizontalLine";

const ShowContent = styled.div`
  text-align: left;
  font-size: 2.5rem;
  font-weight: bold;
  line-height: 1.2;
  padding: 0.4rem 0 0 1.1rem;
  color: white;
  background-color: #010101;
  font-family: "Happiness-Sans-Title", sans-serif;
`;

const NoneShow = styled.div`
  color: white;
`;

function Search() {
  const [shows, setShows] = useState(null);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  const onSearch = useCallback(
    (searchString) => setSearchQuery(searchString),
    []
  );

  console.log(searchQuery);

  useEffect(() => {
    const fetchShows = async () => {
      try {
        setError(null);
        setShows(null);
        const url = searchQuery ? `/api/shows/${searchQuery}` : "/api/shows";
        // const response = await axios.get(`http://exhi-lover.com${url}`);
        const response = await axios.get(`http://localhost:5100${url}`);
        setShows(response.data);
      } catch (e) {
        setError(e);
      }
    };
    fetchShows();
  }, [searchQuery]);

  if (error) return <div>ERR</div>;
  if (!shows)
    return (
      <>
        <SearchBar onSearch={onSearch} />
        <HorizontalLine key="horizontal-line" />
      </>
    );

  return (
    <>
      <SearchBar onSearch={onSearch} />
      <HorizontalLine key="horizontal-line" />
      <ShowContent>Exhibitions</ShowContent>
      <PostTemplate shows={shows} />
    </>
  );
}

export default Search;
