import * as React from "react";
import { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";

import Post from "../post/Post";
import { era, genre, takePhoto, artMovement, orient, database } from "./Tags";
import TagsCheckBox from "./TagsCheckBox";
import HorizontalLine from "../common/HorizontalLine";
import PostTemplate from "../post/PostTemplate";

const Box = styled.div`
  algin-item: left;
  width: 100%;
  padding: 0.4rem 0 0 1.1rem;
  margin: 0 auto;
  padding: 5px 5px 30px 5px;
`;

function HashTag() {
  const [shows, setShows] = useState(null);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  const [selectedItems, setSelectedItems] = useState([]);
  const [selectedPhoto, setSelectedPhoto] = useState([]);
  const [selectedOrient, setSelectedOrient] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState([]);
  const [selectedEra, setSelectedEra] = useState([]);
  const [selectedArtMovement, setSelectedArtMovement] = useState([]);

  const handleCheckedPhoto = (items) => {
    setSelectedPhoto(items);
  };
  const handleCheckedOrient = (items) => {
    setSelectedOrient(items);
  };
  const handleCheckedGenre = (items) => {
    setSelectedGenre(items);
  };
  const handleCheckedEra = (items) => {
    setSelectedEra(items);
  };
  const handleCheckedArtMovement = (items) => {
    setSelectedArtMovement(items);
  };

  useEffect(() => {
    setSelectedItems((prevSelectedItems) => {
      const items = [
        ...selectedPhoto,
        ...selectedOrient,
        ...selectedGenre,
        ...selectedEra,
        ...selectedArtMovement,
      ];
      return items;
    });
  }, [
    selectedPhoto,
    selectedOrient,
    selectedGenre,
    selectedEra,
    selectedArtMovement,
  ]);

  useEffect(() => {
    console.log("selectedItems changed:", selectedItems);
    const tagIds = selectedItems.reduce((acc, tagName) => {
      if (database.hasOwnProperty(tagName)) {
        acc[tagName] = database[tagName];
      }
      return acc;
    }, {});
    console.log(tagIds);
  }, [selectedItems]);

  useEffect(() => {
    const fetchShows = async () => {
      try {
        setError(null);
        setShows(null);
        const url = searchQuery
          ? `/api/exhibition_showId/${searchQuery}`
          : "/api/shows";
        // const response = await axios.get(`https://exhi-lover.com${url}`);
        const response = await axios.get(`http://localhost:5100${url}`);
        setShows(response.data);
      } catch (e) {
        setError(e);
      }
    };
    fetchShows();
  }, [searchQuery]);

  return (
    <>
      <Box>
        <HorizontalLine />
        <TagsCheckBox
          title={"촬영 가능 여부"}
          items={takePhoto}
          handleCheckedItems={handleCheckedPhoto}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"지역별"}
          items={orient}
          handleCheckedItems={handleCheckedOrient}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"장르별"}
          items={genre}
          handleCheckedItems={handleCheckedGenre}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"시대별"}
          items={era}
          handleCheckedItems={handleCheckedEra}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"미술사조"}
          items={artMovement}
          handleCheckedItems={handleCheckedArtMovement}
        />
        <HorizontalLine />
      </Box>
    </>
  );
}

export default HashTag;
