import * as React from "react";
import Done from "@mui/icons-material/Done";
import styled from "styled-components";

const Box = styled.div`
  background-color: #000000;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  justify-content: space-between;
  align-items: flex-start;

  button {
    margin-left: 0.7rem;
    transform: translateY(-3px);
    font-size: 0.9rem;
    background-color: #222222;
    color: white;
    border: none;
    padding: 5px 10px;
    border-radius: 5px;
    cursor: pointer;
    transition: background-color 0.2s;
    &:hover {
      background-color: #333333;
    }
  }
  p {
    font-size: 1.4rem;
    color: white;
    margin: 0;
    padding: 0;
  }
  font-family: "Happiness-Sans-Title", sans-serif;
`;

const Container = styled.div`
  display: flex;
  flex-wrap: wrap;
  line-height: 1.2;
  font-family: "Happiness-Sans-Title", sans-serif;
`;

const Item = styled.div`
  display: flex;
  align-items: center;
  font-size: 0.97rem;
  margin-right: 0.45rem;
  margin-bottom: 10px;
  border-radius: 11px;
  padding: 3px 8px;
  background-color: ${({ checked }) => (checked ? "white" : "black")};
  color: ${({ checked }) => (checked ? "black" : "white")};
  border: ${({ checked }) =>
    checked ? "0.11rem solid black" : "0.09rem solid #afafaf"};
  cursor: pointer;
  svg {
    font-size: 1.1rem;
    color: black;
    margin-right: 1px;
  }
`;

const TagsCheckBox = ({ title, items, handleCheck }) => {
  const [value, setValue] = React.useState([]);

  const handleItemClick = (item) => {
    if (value.includes(item)) {
      setValue((val) => val.filter((text) => text !== item));
    } else {
      setValue((val) => [...val, item]);
    }
    handleCheck(value);
  };

  const handleReset = () => {
    setValue([]);
    handleCheck([]); // call the callback function with an empty array
  };

  return (
    <Box>
      <p>
        {title}
        <button onClick={handleReset}>선택 초기화</button>
      </p>
      <br />
      <Container>
        {items.map((item) => (
          <Item
            key={item}
            checked={value.includes(item)}
            onClick={() => handleItemClick(item)}
          >
            {value.includes(item) && <Done />}
            {item}
          </Item>
        ))}
      </Container>
    </Box>
  );
};

export default TagsCheckBox;
