import React, { useState, useEffect } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";
import axios from "axios";
import Post from "../post/Post";
import PostTemplate from "../post/PostTemplate";
import SearchBar from "../search/SearchBar";
import styled from "styled-components";
import DetailTemplate from "../detail/DetailTemplate";

const HorizonLine = styled.hr`
  width: 90%;
  border: none;
  border-top: 1px solid #464646; /* change the color to blue and thickness to 2px */
  margin: 1rem auto;
`;
const ShowContent = styled.div`
  text-align: left;
  font-size: 2.5rem;
  font-weight: bold;
  line-height: 1.2;
  padding: 0.4rem 0 0 1.1rem;
  color: white;
  background-color: #010101;
  font-family: "Happiness-Sans-Title", sans-serif;
`;

function Search() {
  const [shows, setShows] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchShows = async () => {
      try {
        setError(null);
        setShows(null);
        const response = await axios.get(
          "https://www.exhi-lover.com/api/shows"
        );
        setShows(response.data);
      } catch (e) {
        setError(e);
      }
    };
    fetchShows();
  }, []);

  if (error) return <div>ERR</div>;
  if (!shows) return null;
  return (
    <>
      <SearchBar />
      <HorizonLine />
      {/* 임시 */}
      <DetailTemplate shows={shows}></DetailTemplate>
      <ShowContent>Exhibitions</ShowContent>
      <PostTemplate shows={shows} />
    </>
  );
}

export default Search;
