import React, { useState, useEffect } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Paper from "@mui/material/Paper";
import Post from "./Post";
import Typography from "@mui/material/Typography";
import axios from "axios";

function Search() {
  const [shows, setShows] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchShows = async () => {
      try {
        // 요청이 시작 할 때에는 error 와 users 를 초기화하고
        setError(null);
        setShows(null);

        //local에서 할때
        //const response = await axios.get("http://localhost:5100/api/shows");
        //build할때
        const response = await axios.get("https://exhi-lover.com/api/shows");
        setShows(response.data); // 데이터는 response.data 안에 들어있습니다.
        console.log(shows);
      } catch (e) {
        setError(e);
      }
    };

    fetchShows();
  }, []);

  if (error) return <div>에러가 발생했습니다</div>;
  if (!shows) return null;

  return (
    <>
      <Paper
        sx={{
          backgroundColor: "#2a2a2a",
          //width: "30ch",
          width: "90%",
          maxWidth: "30ch",
          margin: "auto",
          paddingBottom: "10px",
          paddingTop: "10px",
        }}
      >
        <Box
          component="form"
          sx={{
            "& .MuiTextField-root": { m: 1, width: "25ch" },
            margin: "auto",
          }}
          noValidate
          autoComplete="off"
        >
          <Typography variant="subtitle2" sx={{ color: "#EC6C03" }}>
            전시회, 작가 이름 검색
          </Typography>

          <TextField
            id="standard-search"
            type="search"
            variant="standard"
            color="warning"
            inputProps={{ style: { color: "#ECD3BF" } }}
            focused
          />
        </Box>
      </Paper>
      <Box height={"75px"}></Box>
      <Paper
        sx={{
          backgroundColor: "#1b1b1b", //#1b1b1b
          margin: "auto",
          width: "100%",
          paddingBottom: "5px",
        }}
      >
        {shows.map((c) => {
          return (
            <Post
              show_name={c.show_name}
              show_artist={c.show_artist}
              show_search={c.show_search}
              show_term={c.show_term}
              show_num={c.show_num}
              show_place={c.show_place}
              show_place_eng={c.show_place_eng}
              show_place_detail={c.show_place_detail}
              show_price={c.show_price}
              show_link={c.show_link}
              show_hashtag={c.show_hashtag}
              show_imgs={c.show_imgs}
            />
          );
        })}
      </Paper>
      <Box height={"55px"}></Box>
    </>
  );
}

export default Search;
