import React, { useEffect, useState } from "react";
import styled from "styled-components";
import KakaoMap from "./KaKaoMap";
import Marker from "./Marker";

const MapBox = styled.div`
  width: 100%;
  align-items: center;
  margin: 0;

  @media (max-width: 560px) {
    height: calc(110vw + 50px);
  }

  @media (min-width: 561px) {
    height: 666px;
  }
`;

const MapTemplate = () => {
  const [state, setState] = useState({
    center: {
      lat: 37.56649,
      lng: 126.978488,
    },
    errMsg: null,
    isLoading: true,
  });

  const { positions, isLoading: markersLoading } = Marker(); // Marker에서 로딩 상태와 positions을 받음

  const [flag, setFlag] = useState(false); // true -> marker 활성화

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setState((prev) => ({
            ...prev,
            center: {
              lat: position.coords.latitude, // 위도
              lng: position.coords.longitude, // 경도
            },
            isLoading: false,
          }));
        },
        (err) => {
          setState((prev) => ({
            ...prev,
            errMsg: err.message,
            isLoading: false,
          }));
        }
      );
    } else {
      setState((prev) => ({
        ...prev,
        errMsg: "geolocation을 사용할 수 없어요..",
        isLoading: false,
      }));
    }
  }, []);

  useEffect(() => {
    if (!state.isLoading && !markersLoading) {
      setFlag(true); // 모든 로딩이 완료되었을 때 flag를 true로 설정
    }
  }, [state.isLoading, markersLoading]);

  return (
    <MapBox>
      <KakaoMap
        lat={state.center.lat}
        lng={state.center.lng}
        showMarker={flag}
        galleriesMarker={positions}
      />
    </MapBox>
  );
};

export default MapTemplate;
