import React, { useEffect, useState } from "react";
import styled from "styled-components";
import line from "../../lib/icon/line.png";

const Test = ({ open, galleryId }) => {
  const [currentHeight, setCurrentHeight] = useState("100");

  useEffect(() => {
    if (galleryId === undefined) {
      setCurrentHeight("20");
    } else {
      if (open === true) {
        setCurrentHeight("200");
      } else if (open === false) {
        setCurrentHeight("100");
      }
    }
  }, [open, galleryId]);

  return (
    <div
      style={{
        display: "flex",
        // alignItems: "center", // Center vertically
        justifyContent: "center", // Center horizontally
        paddingTop: "10px",
        height: currentHeight + "px",
        // height: "20px",
        width: "100%",
        background: "black",
        position: "absolute",
        borderRadius: "15px 15px 0 0",
        bottom: "0",
        zIndex: "999",
      }}
    >
      <img src={line} alt="Line" style={{ width: "120px", height: "5px" }} /> hi
    </div>
  );
};

export default Test;
