import * as React from "react";
import styled from "styled-components";
import palette from "../../lib/styles/palette";

const PostBlock = styled.div`
  padding: 10px 10px 10px 20px;
  width: 100%;
  max-width: 30rem;
  margin: 0 auto;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
`;

const ColorChange = styled.div`
  background: #1b1b1b;
  &:hover {
    background: #ec6c03;
    text-decoration: underline;
    h1 {
      color: black;
    }
    h2 {
      color: black;
    }
    h3 {
      color: black;
    }
    h4 {
      color: black;
    }
    strong {
      color: black;
    }
  }
`;
const ContextBox = styled.div`
padding: 12px;
  max-width: 23rem;
  max-height: 25rem;
  margin:10px
  display: flex;
  flex: 1 0 auto;
  flex-direction: column;

`;

const PostTitle = styled.div`
  text-align: left;
  color: white;
  strong {
    color: #ec6c03;
  }
`;

const PostContent = styled.div`
  text-align: left;
  color: white;
`;

const ImageBlock = styled.div`
  max-width: 23rem;
  margin: 0 auto;
  max-height: 25rem;

  overflow: hidden;
  img {
    max-height: initial;
    widht: 80vh;
  }
`;

const Post = () => {
  return (
    <>
      <PostBlock>
        <ColorChange>
          <ContextBox>
            <PostTitle>
              <h1>
                감각의 공간
                <br />
                <strong>워치 앤 칠 2.0</strong>
              </h1>
            </PostTitle>
            <PostContent>
              <h3>기간</h3>
              <h4>태그1 태그2 태그3 태그4</h4>
            </PostContent>
          </ContextBox>

          <ImageBlock>
            <img
              src="/upload/seoul/mmca/감각의 공간, 워치 앤 칠 2.0/1.png"
              alt=""
              loading="lazy"
            />
          </ImageBlock>
        </ColorChange>
      </PostBlock>
    </>
  );
};

export default Post;
