import { useState, useEffect } from "react";
import axios from "axios";
import {
  fetchAllShows,
  fetchShowByShowId,
  fetchShowIdByTagId,
} from "../../lib/api/api";

function IdToShow({ TagItems }) {
  const [shows, setShows] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const tagIdString = TagItems.join("&");
    const fetchShows = async () => {
      if (tagIdString !== "") {
        try {
          setError(null);
          const showsIds = await fetchShowIdByTagId(tagIdString);
          console.log(showsIds.split("&"));
          const tags = await axios.get(
            `http://localhost:5100/api/exhibtion/shows/${showsIds.split("&")}`
          );
          console.log(tags);
          const getShowResponse = await fetchShowByShowId(showsIds);
          console.log(getShowResponse);
          setShows(getShowResponse);
        } catch (e) {
          setError(e);
        }
      } else {
        const show = await fetchAllShows();
        setShows(show);
      }
    };

    fetchShows();
  }, [TagItems]);

  return { shows };
}

export default IdToShow;
