import * as React from "react";
import Box from "@mui/joy/Box";
import Done from "@mui/icons-material/Done";
import styled from "styled-components";

import { era } from "./Tags";

const Container = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const Item = styled.div`
  display: flex;
  align-items: center;
  margin-right: 10px;
  margin-bottom: 10px;
  border-radius: 20px;
  min-height: 32px;
  padding: 4px 8px;
  background-color: ${({ checked }) => (checked ? "white" : "black")};
  color: ${({ checked }) => (checked ? "black" : "white")};
  border: ${({ checked }) => (checked ? "1px solid black" : "1px solid white")};
  cursor: pointer;
`;

const CheckIcon = styled(Done)`
  font-size: 16px;
  color: black;
  margin-right: 4px;
`;

function HashTag() {
  const [value, setValue] = React.useState([]);

  const handleItemClick = (item) => {
    if (value.includes(item)) {
      setValue((val) => val.filter((text) => text !== item));
    } else {
      setValue((val) => [...val, item]);
    }
  };

  return (
    <Container>
      <Box>
        {era.map((item) => (
          <Item
            key={item}
            checked={value.includes(item)}
            onClick={() => handleItemClick(item)}
          >
            {value.includes(item) && <CheckIcon />}
            {item}
          </Item>
        ))}
      </Box>
    </Container>
  );
}

export default HashTag;
