import { useState, useEffect } from "react";
import { fetchShowTagsId, fetchTagName } from "../../lib/api/Api";

function FetchShowTags(id) {
  const [tagIds, setTagIds] = useState(null);
  const [tags, setTags] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTagsIds = async () => {
      try {
        setError(null);
        const ids = await fetchShowTagsId(id);
        setTagIds(ids.map((item) => item.tag_id));
      } catch (e) {
        setError(e);
      }
    };
    fetchTagsIds();
  }, [id]);

  useEffect(() => {
    const fetchTags = async () => {
      try {
        setError(null);
        const fetchedTags = await Promise.all(
          tagIds.map(async (tagId) => {
            // console.log(tagId);
            return fetchTagName(tagId);
          })
        );
        setTags(fetchedTags);
      } catch (e) {
        setError(e);
      }
    };

    if (tagIds && tagIds.length > 0) {
      fetchTags();
    }
  }, [tagIds]);
  return tags;
}

export default FetchShowTags;
