import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import DetailViewer from "./DetailViewer";
import SubHeader from "../header/SubHeader";
import Footer from "../common/Footer";

const TemplateBlock = styled.div`
  padding-top: 0.2rem;
  box-sizing: border-box;
  background: #000000;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-flow: row wrap;
`;

const DetailTemplate = ({ color }) => {
  const { showName } = useParams();
  const [show, setShow] = useState(null);
  const [tags, setTags] = useState(null);
  const [tagsId, setTagsId] = useState(null);
  const [error, setError] = useState(null);
  useEffect(() => {
    const fetchShow = async () => {
      try {
        setError(null);
        setShow(null);
        const response = await axios.get(
          // `https://www.exhi-lover.com/api/detail/${showName}`
          `http://localhost:5100/api/detail/${showName}`
        );
        setShow(response.data);
      } catch (e) {
        setError(e);
      }
    };
    fetchShow();
  }, [showName]);

  useEffect(() => {
    const fetchTagsId = async () => {
      try {
        setError(null);
        const tagsIdData = await Promise.all(
          shows.map(async (show) => {
            const url = `/api/exhibition_tags/${show.id}`;
            // const response = await axios.get(`https://exhi-lover.com${url}`);
            const response = await axios.get(`http://localhost:5100${url}`);
            return response.data;
          })
        );
        if (tagsIdData.length > 0) {
          const tagsIdArray = tagsIdData.reduce((acc, innerArray) => {
            innerArray.forEach((row) => {
              const { exhibition_id, tag_id } = row;
              const index = acc.findIndex(
                (obj) => obj.exhibition_id === exhibition_id
              );
              if (index === -1) {
                acc.push({ exhibition_id, tags: [tag_id] });
              } else {
                acc[index].tags.push(tag_id);
              }
            });
            return acc;
          }, []);
          setTagsId(tagsIdArray);
        }
      } catch (e) {
        setError(e);
      }
    };

    fetchTagsId();
  }, [shows]);

  useEffect(() => {
    const fetchTag = async () => {
      try {
        setError(null);
        const tagData = await Promise.all(
          tagsId.map(async ({ exhibition_id, tags }) => {
            const tagNames = await Promise.all(
              tags.map(async (tagId) => {
                const url = `/api/tags/${tagId}`;
                const response = await axios.get(`http://localhost:5100${url}`);
                return response.data[0].tag_name;
              })
            );
            return { exhibition_id, tagNames };
          })
        );
        setTags(
          tagData.reduce((acc, { exhibition_id, tagNames }) => {
            acc[exhibition_id] = tagNames;
            return acc;
          }, {})
        );
      } catch (e) {
        setError(e);
      }
    };

    if (tagsId !== null) {
      fetchTag();
    }
  }, [tagsId]);

  if (error) return <div>ERR</div>;
  if (!show) return null;
  return (
    <>
      <SubHeader />
      <TemplateBlock>
        <DetailViewer show={show} color={color} />
      </TemplateBlock>
      <Footer />
    </>
  );
};

export default DetailTemplate;
