import * as React from "react";
import { useState } from "react";
import Avatar from "@mui/joy/Avatar";
import Box from "@mui/joy/Box";
import Card from "@mui/joy/Card";
import CardCover from "@mui/joy/CardCover";
import Typography from "@mui/joy/Typography";
import Link from "@mui/joy/Link";
import { purple } from "@mui/material/colors";
import Button from "@mui/material/Button";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import styled from "@emotion/styled";
import Stack from "@mui/material/Stack";
import Detail from "./Detail";

const ColorButton = styled(Button)(({ theme }) => ({
  color: "white",
  backgroundColor: purple[500],
  "&:hover": {
    backgroundColor: purple[700],
  },
}));

function Post({
  show_name,
  show_artist,
  show_search,
  show_term,
  show_num,
  show_place,
  show_place_eng,
  show_place_detail,
  show_price,
  show_link,
  show_hashtag,
  show_imgs,
}) {
  const imgSrc = "/upload/seoul/" + show_place_eng + "/" + show_name + "/1.png";
  const [open, setOpen] = useState(false);
  const handleClose = () => {
    setOpen(false);
  };
  const handleClickOpen = () => {
    setOpen(true);
  };

  return (
    <Card
      sx={{
        //minWidth: 150,
        //width: "30ch"
        width: "95%",
        height: "auto",
        margin: "auto",
        marginTop: "20px",
        bgcolor: "#131313", //initial
        boxShadow: "none",
        "--Card-padding": "8px",
        // post.js 영역 넓이
      }}
    >
      <Button onClick={handleClickOpen} sx={{ margin: "auto", width: "100%" }}>
        <Box
          sx={{
            position: "relative", //이미지
            margin: "auto",
            marginTop: "10px",
            width: "100%",
            backgroundColor: "primary.dark",
            "&:hover": {
              backgroundColor: "primary.main",
              opacity: [0.9, 0.8, 0.7],
            },
          }}
        >
          <Box
            component="img"
            sx={{
              width: "100%",
              margin: "auto", //박스를 쓰지말고 img 태그를 사용하면서 alt, width, height 설정해주고
            }}
            src={imgSrc}
          />

          <CardCover
            className="gradient-cover"
            sx={{
              "&:hover, &:focus-within": {
                opacity: 1,
              },
              //opacity: 0, 이걸 넣으면 마우스를 올리거나 클리하면 생기고 때면 사라짐. 즉 나한테 필요 없뜸

              paddingRight: "10px",
            }}
          >
            {/* The first box acts as a container that inherits style from the CardCover */}
          </CardCover>
        </Box>
      </Button>

      {/* 전시 팝업 시작 */}
      <Button onClick={handleClickOpen}>
        <Box marginBottom={"5px"} sx={{ width: "100%" }}>
          <Typography
            textAlign={"left"}
            sx={{
              margin: "auto",
              paddingBottom: "3px",
              overflow: "hidden",
              textOverflow: "ellipsis",
              color: "#eeeeee",
            }}
          >
            {show_name}
          </Typography>
          <Typography
            noWrap
            textAlign={"left"}
            sx={{
              margin: "left",
              overflow: "hidden",
              textOverflow: "ellipsis",
              color: "#eeeeee",
              paddingTop: "1px",
            }}
          >
            {show_artist}
          </Typography>
        </Box>
      </Button>
      {/* 전시 팝업 끝 */}

      <Dialog
        fullWidth={"true"}
        maxWidth={"xl"}
        open={open}
        onClose={handleClose}
        scroll={"paper"}
        aria-labelledby="scroll-dialog-title"
        aria-describedby="scroll-dialog-description"
      >
        <Detail
          show_name={show_name}
          show_artist={show_artist}
          show_search={show_search}
          show_term={show_term}
          show_num={show_num}
          show_place={show_place}
          show_place_eng={show_place_eng}
          show_place_detail={show_place_detail}
          show_price={show_price}
          show_hashtag={show_hashtag}
          show_imgs={show_imgs}
        />

        <DialogActions>
          <Stack spacing={2} direction="row">
            <ColorButton variant="outlined" size="small">
              <Link
                href={show_link}
                underline="none"
                variant="button"
                target={"_blank"}
                margin="left"
                fontSize="sm"
              >
                Booking
              </Link>
            </ColorButton>
            <ColorButton variant="outlined" onClick={handleClose} size="small">
              Close
            </ColorButton>
          </Stack>
        </DialogActions>
      </Dialog>
    </Card>
  );
}

export default Post;
