/* eslint-disable jsx-a11y/anchor-is-valid */
import * as React from "react";
import AspectRatio from "@mui/joy/AspectRatio";
import Avatar from "@mui/joy/Avatar";
import Box from "@mui/joy/Box";
import Card from "@mui/joy/Card";
import CardOverflow from "@mui/joy/CardOverflow";
import Typography from "@mui/joy/Typography";
import { Link } from "@mui/material";
import { styled } from "@mui/material/styles";

export default function Detail({
  show_name,
  show_artist,
  show_search,
  show_term,
  show_num,
  show_place,
  show_place_eng,
  show_place_detail,
  show_price,
  show_link,
  show_hashtag,
  show_imgs,
}) {
  //const imgSrc = "/upload/" + show_place_eng + "/seoul/" + show_name + "/1.png";
  const imgSrc = "/upload/seoul/" + show_place_eng + "/" + show_name + "/1.png";
  const Div = styled("div")(({ theme }) => ({
    ...theme.typography.button,
    backgroundColor: "grey",
    padding: theme.spacing(1),
  }));

  return (
    <Card
      sx={{
        "--Card-radius": (theme) => theme.vars.radius.xs,
        bgcolor: "#ffffff",

        margin: "auto",
      }}
    >
      <CardOverflow>
        <Box
          component="img"
          sx={{
            width: "100%",
            margin: "auto", //박스를 쓰지말고 img 태그를 사용하면서 alt, width, height 설정해주고
          }}
          src={imgSrc}
        />
      </CardOverflow>
      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          mx: -1,
          my: 1,
        }}
      >
        <Box
          sx={{
            display: "flex",
            alignItems: "center",
            gap: 0.5,
            mx: "auto",
            backgroundColor: "red",
          }}
        >
          {[...Array(5)].map((_, index) => (
            <Box
              key={index}
              sx={{
                borderRadius: "50%",
                width: `max(${6 - index}px, 3px)`,
                height: `max(${6 - index}px, 3px)`,
                bgcolor: index === 0 ? "primary.solidBg" : "background.level3",
              }}
            />
          ))}
        </Box>
      </Box>
      <Box sx={{ display: "flex", alignItems: "center", pb: 1.5, gap: 1 }}>
        <Box
          sx={{
            position: "relative",
            "&:before": {
              content: '""',
              position: "absolute",
              top: 0,
              left: 0,
              bottom: 0,
              right: 0,
              m: "-2px",
              borderRadius: "50%",
              background:
                "linear-gradient(45deg, #f09433 0%,#e6683c 25%,#dc2743 50%,#cc2366 75%,#bc1888 100%)",
            },
          }}
        >
          <Avatar
            size="sm"
            src="/static/logo.png"
            sx={{ p: 0.5, border: "1px solid", borderColor: "background.body" }}
          />
        </Box>
        <Typography fontWeight="xl">{show_place}</Typography>
      </Box>
      <Typography fontSize="lg">{show_name}</Typography>
      <Typography fontSize="sm">참여 작가: {show_artist}</Typography>
      <Typography fontSize="sm">기간: {show_term}</Typography>
      <Typography fontSize="sm">작품 수: {show_num}</Typography>
      <Typography fontSize="sm">상세 장소: {show_place_detail}</Typography>
      <Typography fontSize="sm">가격: {show_price}</Typography>

      <Typography fontSize="sm" sx={{ margin: "auto", textalgin: "left" }}>
        <Div>해쉬 태그: </Div>
        {show_hashtag}
      </Typography>
    </Card>
  );
}
