import * as React from "react";
import styled from "styled-components";
import palette from "../../lib/styles/palette";
import { useState } from "react";
import Link from "@mui/joy/Link";
import { Button } from "@mui/material";

const PostBlock = styled.div`
  padding: 5px 5px 30px 5px;
  width: 100%;
  max-width: 26rem;
  margin: 0 auto;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
`;

const HoverEffect = styled.div`
  background: #131313;
  &:hover {
    background: #ec6c03;
    text-decoration: underline;
    h1 {
      color: black;
    }
    h2 {
      color: black;
    }
    h3 {
      color: black;
    }
    h4 {
      color: black;
    }
    h5 {
      color: black;
    }
    strong {
      color: black;
    }
    img {
      transform: scale(1.05);
    }
  }
`;
const ContextBox = styled.div`
  box-sizing: border-box;
  padding: 13px 13px 0px 13px;
  width: 100%;
  max-width: 23rem;
  max-height: 25rem;
  margin: 9px;
  display: flex;
  flex: 1 0 auto;
  flex-direction: column;
`;

const PostTitle = styled.div`
  text-align: left;
  color: white;
  strong {
    color: #ec6c03;
  }
  font-family: "Happiness-Sans-Title", sans-serif;
`;

const PostContent = styled.div`
  text-align: left;
  color: white;
  font-family: "Happiness-Sans-Title", sans-serif;
`;

const ImageBlock = styled.div`
  max-width: 23rem;
  margin: 0 auto;
  max-height: 25rem;

  overflow: hidden;
  img {
    max-height: initial;
    widht: 80vh;
    transition: transform 0.5s ease-in-out;
  }
`;

const Post = ({ show, index }) => {
  const {
    show_name,
    show_artist,
    show_search,
    show_term,
    show_num,
    show_city,
    show_place,
    show_place_eng,
    show_place_detail,
    show_price,
    show_link,
    show_hashtag,
    show_imgs,
  } = show;
  const imgSrc = "/upload/seoul/" + show_place_eng + "/" + show_name + "/1.png";
  const [open, setOpen] = useState(false);
  const handleClose = () => {
    setOpen(false);
  };
  const handleClickOpen = () => {
    setOpen(true);
  };
  return (
    <>
      <PostBlock>
        <HoverEffect>
          <ContextBox>
            <PostTitle>
              <h1>
                감각의 공간
                <br />
                <strong>워치 앤 칠 2.0</strong>
              </h1>
            </PostTitle>
            <PostContent>
              <h4>2022.02.10 - 2023.01.12</h4>
              <h4>태그1 태그2 태그3 태그4</h4>
            </PostContent>
          </ContextBox>

          <ImageBlock>
            <img
              src="/upload/seoul/mmca/감각의 공간, 워치 앤 칠 2.0/3.png"
              alt=""
              loading="lazy"
            />
          </ImageBlock>
        </HoverEffect>
      </PostBlock>
    </>
  );
};

export default Post;
