import React from "react";
import MainButton from "../component/button/MainButton";
import MainHeader from "../component/header/MainHeader";
import styled from "styled-components";

const MainImage = styled.div`
  width: 100%;
  height: 0;
  padding-bottom: 56.25%; /* maintain 16:9 aspect ratio */
  position: relative;
  overflow: hidden;
  margin: 0 auto;
  img {
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.6s ease;
  }
`;

const InformBox = styled.div`
  background: white;
  padding: 5px 5px 5px 20px;
  position: relative;
  z-index: 1;
`;

const InformTitle = styled.div`
  font-family: "Happiness-Sans-Title", sans-serif;
`;

const InfromContext = styled.div`
  font-family: "Happiness-Sans-Title", sans-serif;
`;

function MainPage() {
  return (
    <>
      <MainHeader />
      <MainImage>
        <img src="/upload/main/3.png" alt="" loading="lazy" />
      </MainImage>
      <InformBox>
        <InformTitle>안녕하세요</InformTitle>
        <InfromContext>hihi</InfromContext>
      </InformBox>
      <MainButton />
    </>
  );
}

export default MainPage;
