import * as React from "react";
import { useState, useEffect } from "react";
import styled from "styled-components";

import { era, genre, takePhoto, artMovement, orient, database } from "./Tags";
import TagsCheckBox from "./TagsCheckBox";
import HorizontalLine from "../common/HorizontalLine";

const Box = styled.div`
  algin-item: left;
  width: 100%;
  padding: 0.4rem 0 0 1.1rem;
  margin: 0 auto;
  padding: 5px 5px 30px 5px;
`;

function HashTag() {
  const [checkedPhoto, setCheckedPhoto] = useState([]);
  const [checkedOrient, setCheckedOrient] = useState([]);
  const [checkedGenre, setCheckedGenre] = useState([]);
  const [checkedEra, setCheckedEra] = useState([]);
  const [checkedArtMovement, setCheckedArtMovement] = useState([]);
  const [checkedItems, setCheckedItems] = useState([]);

  const handlePhotoCheck = (items) => {
    setCheckedPhoto(items);
  };
  const handleOrientCheck = (items) => {
    setCheckedOrient(items);
  };
  const handleGenreCheck = (items) => {
    setCheckedGenre(items);
  };
  const handleEraCheck = (items) => {
    setCheckedEra(items);
  };
  const handleArtMovementCheck = (items) => {
    setCheckedArtMovement(items);
  };

  useEffect(() => {
    setCheckedItems();
  });

  return (
    <>
      <Box>
        <HorizontalLine />
        <TagsCheckBox
          title={"촬영 가능 여부"}
          items={takePhoto}
          handleCheck={handlePhotoCheck}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"지역별"}
          items={orient}
          handleCheck={handleOrientCheck}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"장르별"}
          items={genre}
          handleCheck={handleGenreCheck}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"시대별"}
          items={era}
          handleCheck={handleEraCheck}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"미술사조"}
          items={artMovement}
          handleCheck={handleArtMovementCheck}
        />
        <HorizontalLine />
      </Box>
    </>
  );
}

export default HashTag;
