import { createAction, handleActions } from "redux-actions";
import createRequestSaga, {
  createRequestActionTypes,
} from "../lib/createRequestSaga";
import * as postsApi from "../lib/api/post";
import { takeLatest } from "redux-saga/effects";

const [VIEW_POST, VIEW_POST_SUCCES, VIEW_POST_FAILURE] =
  createRequestActionTypes("post/VIEW_POST");

export const viewPost = createAction(VIEW_POST);

const viewPostSaga = createRequestSaga(VIEW_POST, postsApi.viewPost);
export function* postSaga() {
  yield takeLatest(VIEW_POST, viewPostSaga);
}

const initialState = {
  post: null,
  error: null,
};

const post = handleActions(
  {
    [VIEW_POST_SUCCESS]: (state, { payload: post }) => ({
      ...state,
      post,
    }),
    [VIEW_POST_FAILURE]: (state, { payload: error }) => ({
      ...state,
      error,
    }),
  },
  initialState
);

export default post;
