import React, { useState } from "react";
import Button from "@mui/material/Button";
import ButtonGroup from "@mui/material/ButtonGroup";
import Box from "@mui/material/Box";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import Search from "../items/Search";
import HashTag from "../items/HashTag";
import Map from "../map/Map";

const theme = createTheme({
  palette: {
    white: {
      main: "#ffffff",
      contrastText: "#ffffff",
    },
  },
});

function MainButton() {
  const [stat, changeStat] = useState(0);

  const activateTextField = () => {
    changeStat(0);
  };
  const activateMap = () => {
    changeStat(1);
  };
  const activateHashTag = () => {
    changeStat(2);
  };

  return (
    <div>
      <Box
        sx={{
          display: "flex",
          backgroundColor: "#000000",
          flexDirection: "column",
          alignItems: "center",
          marginLeft: "10px",
          "& > *": {
            m: 3,
          },
        }}
      >
        <ThemeProvider theme={theme}>
          <ButtonGroup
            size="large"
            variant="text"
            aria-label="text button group"
            color="white"
            sx={{
              paddingRight: "16px",
              paddingTop: "2.5rem",
            }}
          >
            <Button onClick={activateTextField}>Search</Button>
            <Button onClick={activateMap}>Map</Button>
            <Button onClick={activateHashTag}>HashTag</Button>
          </ButtonGroup>
        </ThemeProvider>
      </Box>
      <div>{stat === 0 && <Search />}</div>
      <div>{stat === 1 && <Map />} </div>
      <div>{stat === 2 && <HashTag />}</div>
    </div>
  );
}

export default MainButton;
