import { useState, useEffect } from "react";
import { database } from "./Tags";
import styled from "styled-components";
import axios from "axios";
import colorSet from "../../lib/styles/colorSet";
import TagsCheckBox from "./TagsCheckBox";
import HorizontalLine from "../common/HorizontalLine";
import PostTemplate from "../post/PostTemplate";

function IdToShow({ TagItems }) {
  const [tagIdsUrl, setTagIdsUrl] = useState("");
  const [showsId, setShowsId] = useState(null);
  const [shows, setShows] = useState(null);
  const [tags, setTags] = useState(null);
  const [tagsId, setTagsId] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const tagObject = TagItems.reduce((acc, tagName) => {
      if (database.hasOwnProperty(tagName)) {
        acc[tagName] = database[tagName];
      }
      return acc;
    }, {});
    const tagIds = Object.values(tagObject).filter(
      (value) => typeof value === "number"
    );
    const tagIdsString = tagIds.join("&");
    tagIds.sort((a, b) => a - b);
    const url = tagIdsString
      ? `/api/exhibition_showId/${tagIdsString}`
      : "/api/shows";

    const fetchShowsId = async () => {
      try {
        setError(null);
        setShowsId(null);
        if (tagIdsUrl !== "") {
          // const response = await axios.get(`https://exhi-lover.com${tagIdsUrl}`);
          const response = await axios.get(`http://localhost:5100${tagIdsUrl}`);
          const exhibitionIds = response.data.map(
            ({ exhibition_id }) => exhibition_id
          );
          setShowsId(exhibitionIds);
        }
      } catch (e) {
        setError(e);
      }
    };

    const fetchShow = async () => {
      try {
        setError(null);
        setShows(null);
        const url = showsId ? `/api/shows_id/:showId${showsId}` : "/api/shows";
        // const response = await axios.get(`https://exhi-lover.com${url}`);
        const response = await axios.get(`http://localhost:5100${url}`);
        setShows(response.data);
      } catch (e) {
        setError(e);
      }
    };

    const fetchTagsId = async () => {
      try {
        setError(null);
        const tagsIdData = await Promise.all(
          shows.map(async (show) => {
            const url = `/api/exhibition_tags/${show.id}`;
            // const response = await axios.get(`https://exhi-lover.com${url}`);
            const response = await axios.get(`http://localhost:5100${url}`);
            return response.data;
          })
        );
        if (tagsIdData.length > 0) {
          const tagsIdArray = tagsIdData.reduce((acc, innerArray) => {
            innerArray.forEach((row) => {
              const { exhibition_id, tag_id } = row;
              const index = acc.findIndex(
                (obj) => obj.exhibition_id === exhibition_id
              );
              if (index === -1) {
                acc.push({ exhibition_id, tags: [tag_id] });
              } else {
                acc[index].tags.push(tag_id);
              }
            });
            return acc;
          }, []);
          setTagsId(tagsIdArray);
        }
      } catch (e) {
        setError(e);
      }
    };

    setTagIdsUrl(url);
    fetchShowsId();
    fetchShow();
    fetchTagsId();
  }, [TagItems, tagIdsUrl, shows, showsId]);

  return { showsId, tags };
}

export default IdToShow;
