/*global kakao*/
import React, { useEffect, useRef, useState } from "react";
import Marker from "./Marker";
import { MapMarker } from "react-kakao-maps-sdk";
import icon from "../../lib/icon/loc7.png";

function KakaoMap({ lat, lng, showMarker }) {
  const [loading, setLoading] = useState(true);
  const mapHeight = window.innerWidth >= 560 ? 560 : window.innerWidth;
  const test = Marker();
  console.log(typeof test, test);

  useEffect(() => {
    const container = document.getElementById("map");
    const options = {
      center: new kakao.maps.LatLng(lat, lng),
      level: 3,
    };

    const map = new kakao.maps.Map(container, options);

    if (showMarker) {
      const markerPosition = new kakao.maps.LatLng(lat, lng);
      const marker = new kakao.maps.Marker({
        position: markerPosition,
      });
      marker.setMap(map);
    }

    if (test && test.length) {
      setLoading(false); // Set loading to false when test data is available
      for (var i = 0; i < test.length; i++) {
        const position = test[i];
        const marker = new kakao.maps.Marker({
          map: map,
          position: new kakao.maps.LatLng(position.lat, position.lng),
        });

        const infowindow = new kakao.maps.InfoWindow({
          content: position.title,
        });
        kakao.maps.event.addListener(
          marker,
          "mouseover",
          makeOverListener(map, marker, infowindow)
        );
        kakao.maps.event.addListener(
          marker,
          "mouseout",
          makeOutListener(infowindow)
        );
      }
    }
    // Clean up markers when the component unmounts
    return () => {
      kakao.maps.event.clearListeners(map, "mouseover");
      kakao.maps.event.clearListeners(map, "mouseout");
    };
  }, [lat, lng, showMarker]);

  function makeOverListener(map, marker, infowindow) {
    return function () {
      infowindow.open(map, marker);
    };
  }

  function makeOutListener(infowindow) {
    return function () {
      infowindow.close();
    };
  }

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div id="map" style={{ width: "100%", height: `${mapHeight}px` }}></div>
    </div>
  );
}

export default React.memo(KakaoMap);
